INSERT INTO `posts` (`id`, `title`, `slug`, `introtext`, `thumbnail`, `content`, `views`, `category_id`, `user_id`, `group`, `created_at`, `updated_at`) VALUES
(9, 'Hội thảo xác định chuẩn đầu ra ngành, nghề: Kế toán hành chính sự nghiệp', 'hoi-thao-xac-dinh-chuan-dau-ra-nganh-nghe-ke-toan-hanh-chinh-su-nghiep-1627550015', 'Thực hiện sự phân công của Tổng cục GDNN tại Quyết định số 675/QĐ-TCGDNN ngày 19/6/2018, sáng 06/10/2018 Trường Cao...', 'uploads/images/thumbnail_resize/1.ht_kthcsn.jpg', '<p><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/1.ht%20kthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>ThS. Phạm Văn Nối - B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường ph&aacute;t biểu khai mạc Hội thảo</em></p>\r\n\r\n<p style=\"text-align:justify\">Dự Hội thảo, về ph&iacute;a nh&agrave; trường c&oacute; ThS. Phạm Văn Nối - B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường; ThS. Nguyễn Phi H&ugrave;ng- Ph&oacute; b&iacute; thư Đảng ủy, Ph&oacute; hiệu trưởng nh&agrave; trường; c&ugrave;ng đại diện c&aacute;c ph&ograve;ng đơn vị Đ&agrave;o tạo, T&agrave;i ch&iacute;nh kế to&aacute;n; c&aacute;c giảng vi&ecirc;n khoa Kinh tế.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/2.ht%20kthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>ThS. Nguyễn Phi H&ugrave;ng - Ph&oacute; b&iacute; thư Đảng ủy, Ph&oacute; hiệu trưởng nh&agrave; trường chủ tr&igrave; Hội thảo</em></p>\r\n\r\n<p>Về ph&iacute;a kh&aacute;ch mời l&agrave; c&aacute;c chuy&ecirc;n gia đại diện cho c&aacute;c đơn vị h&agrave;nh ch&iacute;nh sự nghiệp của c&aacute;c v&ugrave;ng: Duy&ecirc;n hải Bắc bộ; H&agrave; Nội, Miền trung.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/3.ht%20kthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>ThS. Nguyễn Anh Ngấn - Trưởng khoa Kinh tế tr&igrave;nh b&agrave;y b&aacute;o c&aacute;o t&oacute;m tắt x&aacute;c định chuẩn đầu ra ng&agrave;nh, nghề Kế to&aacute;n h&agrave;nh ch&iacute;nh sự nghiệp</em></p>\r\n\r\n<p>B&aacute;o c&aacute;o x&aacute;c định chuẩn đầu ra ng&agrave;nh, nghề Kế to&aacute;n h&agrave;nh ch&iacute;nh sự nghiệp, ThS. Nguyễn Anh Ngấn - Trưởng khoa Kinh tế n&ecirc;u r&otilde;: cần x&acirc;y dựng quy định về lượng kiến thức tối</p>\r\n\r\n<p>thiểu cũng như y&ecirc;u cầu về năng lực m&agrave; người học chuy&ecirc;n ng&agrave;nh, nghề n&agrave;y đạt được sau khi tốt nghiệp tr&igrave;nh độ trung cấp, tr&igrave;nh độ cao đẳng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/4.ht%20kthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c chuy&ecirc;n gia thảo luận tại Hội thảo</em></p>\r\n\r\n<p>Tại Hội thảo, c&aacute;c chuy&ecirc;n gia, c&aacute;c giảng vi&ecirc;n đ&atilde; tập trung thảo luận l&agrave;m r&otilde; một số vấn đề trong việc x&aacute;c định chuẩn đầu ra ng&agrave;nh, nghề Kế to&aacute;n H&agrave;nh ch&iacute;nh sự nghiệp như: những vấn đề chung về ng&agrave;nh, nghề; danh mục c&aacute;c vị tr&iacute; việc l&agrave;m; những vấn đề chung của từng vị tr&iacute; việc l&agrave;m; khối lượng kiến thức tối thiểu, c&aacute;c năng lực của từng vị tr&iacute; việc l&agrave;m ứng với tr&igrave;nh độ trung cấp, cao đẳng; lượng kiến thức tối thiểu; y&ecirc;u cầu về năng lực m&agrave; người học đạt được sau khi tốt nghiệp</p>\r\n\r\n<p><strong>Một số chuy&ecirc;n gia ph&aacute;t biểu tại Hội thảo:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/5.ht%20kthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>B&agrave; V&otilde; Thị Hồng Phương &ndash; Giảng vi&ecirc;n trường Cao đẳng LTTP Đ&agrave; Nẵng</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/6.ht%20kthcsn.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>B&agrave; Nguyễn Thị Ngọc H&agrave; &ndash; Giảng vi&ecirc;n trường Cao đẳng N&ocirc;ng nghiệp v&agrave; PTNT Bắc Bộ</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/7.ht%20kthcsn.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>&Ocirc;ng Trịnh Hồng Lĩnh &ndash; Kế to&aacute;n trưởng UBND Phường Ngọc Xuy&ecirc;n &ndash; Quận Đồ Sơn &ndash; TP Hải Ph&ograve;ng</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/8.ht%20kthcsn.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>B&agrave; Phạm Thị Hi&ecirc;n - Trưởng ph&ograve;ng Gi&aacute;o dục quận Đồ Sơn ph&aacute;t biểu</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/9%20hthcsn.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>B&agrave; Nguyễn Thị Th&ugrave;y Dương - Kế to&aacute;n trưởng Bệnh viện đa khoa quận Đồ Sơn</em></p>\r\n\r\n<p>Buổi hội thảo diễn ra th&agrave;nh c&ocirc;ng tốt đẹp, c&aacute;c chuy&ecirc;n gia đ&atilde; thống nhất c&aacute;c vấn đề trong việc x&aacute;c định chuẩn đầu ra ng&agrave;nh, nghề kế to&aacute;n H&agrave;nh ch&iacute;nh sự nghiệp như nội dung hội thảo.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/hoi_thao_chuan_dau_ra/10%20hthcsn.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>ThS. Nguyễn Phi H&ugrave;ng - Ph&oacute; b&iacute; thư Đảng ủy, Ph&oacute; hiệu trưởng nh&agrave; trường ph&aacute;t biểu tổng kết Hội thảo</em></p>\r\n\r\n<p>ThS. Nguyễn Phi H&ugrave;ng thay mặt Ban gi&aacute;m hiệu gửi lời cảm ơn s&acirc;u sắc tới c&aacute;c chuy&ecirc;n gia đ&atilde; l&agrave;m việc nhiệt t&igrave;nh, tr&aacute;ch nhiệm, hiệu quả để thống nhất c&aacute;c vấn đề trong việc x&aacute;c định chuẩn đầu ra của ng&agrave;nh, nghề Kế to&aacute;n H&agrave;nh ch&iacute;nh sự nghiệp.</p>\r\n\r\n<p>Th&agrave;nh c&ocirc;ng của Hội thảo l&agrave; cơ sở để Ban chủ nhiệm x&acirc;y dựng v&agrave; ho&agrave;n thiện chương tr&igrave;nh x&aacute;c định chuẩn đầu ra ng&agrave;nh, nghề Kế to&aacute;n H&agrave;nh ch&iacute;nh sự nghiệp để b&aacute;o c&aacute;o Tổng cục gi&aacute;o dục nghề nghiệp.</p>', 47, 1, 1, 2, '2018-10-05 17:00:00', '2021-07-29 09:13:35'),
(10, 'Trường Cao đẳng kinh tế và công nghệ thực phẩm tổ chức lễ khai giảng năm học 2019-2020', 'truong-cao-dang-kinh-te-va-cong-nghe-thuc-pham-to-chuc-le-khai-giang-nam-hoc-2019-2020-1627549931', 'Hòa trong không khí sôi nổi của hàng triệu học sinh, sinh viên cả nước bước vào năm học mới, sáng ngày 04/9/2019, thầy...', 'uploads/images/thumbnail_resize/13.kg2019.jpg', '<p>H&ograve;a trong kh&ocirc;ng kh&iacute; s&ocirc;i nổi của h&agrave;ng triệu học sinh, sinh vi&ecirc;n cả nước bước v&agrave;o năm học mới, s&aacute;ng ng&agrave;y 04/9/2019, thầy v&agrave; tr&ograve; trường Cao đẳng kinh tế v&agrave; c&ocirc;ng nghệ thực phẩm long trọng tổ chức lễ khai giảng năm học 2019-2020.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/1.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Lễ ch&agrave;o cờ của thầy v&agrave; tr&ograve; nh&agrave; trường</em></p>\r\n\r\n<p style=\"text-align:justify\">Đến dự lễ khai giảng c&oacute; c&aacute;c &ocirc;ng b&agrave; đại diện l&atilde;nh đạo c&aacute;c Trung t&acirc;m li&ecirc;n kết với nh&agrave; trường tr&ecirc;n địa b&agrave;n th&agrave;nh phố Hải Ph&ograve;ng; đại diện c&ocirc;ng an phường; đại diện hội phụ huynh học sinh c&ugrave;ng tập thể c&aacute;n bộ, gi&aacute;o vi&ecirc;n v&agrave; hơn 300 HSSV đang học tập tại trường.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/2.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Th.s Nguyễn Phi H&ugrave;ng - Ph&oacute; b&iacute; thư Đảng ủy, Ph&oacute; Hiệu trưởng nh&agrave; trường đọc thư ch&uacute;c mừng khai giảng của Tổng b&iacute; thư-Chủ tịch nước Nguyễn Ph&uacute; Trọng</em></p>\r\n\r\n<p style=\"text-align:justify\">Trong thư gửi học sinh đầu năm học mới Chủ tịch nước nhấn mạnh: &ldquo;T&ocirc;i mong c&aacute;c thầy gi&aacute;o, c&ocirc; gi&aacute;o, c&aacute;n bộ quản l&yacute;, người lao động ng&agrave;nh gi&aacute;o dục lu&ocirc;n giữ vững bản lĩnh ch&iacute;nh trị, phẩm chất đạo đức, t&acirc;m huyết với nghề, kh&ocirc;ng ngừng đổi mới, s&aacute;ng tạo, đ&oacute;ng g&oacute;p nhiều hơn nữa cho sự nghiệp &#39;trồng người&#39;; mong c&aacute;c em học sinh, sinh vi&ecirc;n tiếp tục ph&aacute;t huy truyền thống hiếu học, &#39;t&ocirc;n sư, trọng đạo&#39; của d&acirc;n tộc, noi theo c&aacute;c thế hệ cha anh đi trước, phấn đấu học tập tốt, r&egrave;n luyện tốt để sau n&agrave;y trở th&agrave;nh những người vừa &#39;hồng&#39;, vừa &#39;chuy&ecirc;n&#39;, như di nguyện của Chủ tịch Hồ Ch&iacute; Minh&quot;.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/3.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Th.s Phạm Văn Nối &ndash; B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường đọc diễn văn khai giảng</em></p>\r\n\r\n<p style=\"text-align:justify\">Một năm học mới đ&atilde; bắt đầu trong kh&ocirc;ng kh&iacute; tưng bừng của những ng&agrave;y m&ugrave;a thu th&aacute;ng 9, trong niềm h&aacute;o hức, phấn khởi của c&aacute;n bộ, gi&aacute;o vi&ecirc;n v&agrave; HSSV nh&agrave; trường, thầy Phạm Văn Nối thay mặt tập thể l&atilde;nh đạo k&ecirc;u gọi to&agrave;n thể c&aacute;n bộ, giảng vi&ecirc;n, gi&aacute;o vi&ecirc;n, nh&acirc;n vi&ecirc;n v&agrave; c&aacute;c HSSV to&agrave;n Trường đo&agrave;n kết, nhất tr&iacute;, nỗ lực, quyết t&acirc;m ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao, c&oacute; những h&agrave;nh động thiết thực v&igrave; mục ti&ecirc;u ph&aacute;t triển nh&agrave; trường bền vững.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/4.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Th.s Phạm Văn Nối &ndash; B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường đ&aacute;nh trống khai trường</em></p>\r\n\r\n<p style=\"text-align:justify\">Cũng tại buổi lễ, thầy Phạm Quang Tr&igrave;nh &ndash; Giảng vi&ecirc;n khoa C&ocirc;ng nghệ th&ocirc;ng tin thay mặt cho tập thể Gi&aacute;o vi&ecirc;n nh&agrave; trường đ&atilde; c&oacute; những chia sẻ về nghề trồng người cao qu&yacute;, hứa phấn đấu học tập, trau dồi chuy&ecirc;n m&ocirc;n ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/5.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Thầy Nguyễn&nbsp;Quang Tr&igrave;nh chia sẻ tại buổi lễ</em></p>\r\n\r\n<p style=\"text-align:justify\">Chia sẻ cảm x&uacute;c của m&igrave;nh trong ng&agrave;y tựu trường, em Phạm Quang Ch&iacute;nh-Học sinh lớp TCKTK55A đại diện cho to&agrave;n thể HSSV trong trường: cảm ơn sự quan t&acirc;m, chỉ bảo tận t&igrave;nh của c&aacute;c thầy c&ocirc; gi&aacute;o v&agrave; tin rằng nh&agrave; trường sẽ l&agrave; nơi gi&uacute;p c&aacute;c em tiếp cận với nguồn tri thức mới, kỹ năng mềm cần thiết để phục vụ cho c&ocirc;ng việc v&agrave; cuộc sống sau n&agrave;y, l&agrave; nơi để ch&uacute;ng em trau dồi bản th&acirc;n trở th&agrave;nh những c&ocirc;ng d&acirc;n c&oacute; &iacute;ch cho đất nước.</p>\r\n\r\n<p style=\"text-align:justify\">Trong lễ khai giảng, b&agrave; Nguyễn Thị Hồng Linh &ndash; Trưởng ph&ograve;ng Khảo th&iacute; kiểm định chất lượng, khoa học v&agrave; hợp t&aacute;c quốc tế đ&atilde; c&ocirc;ng bố những quyết định khen thưởng của nh&agrave; trường đối với c&aacute;c tập thể v&agrave; c&aacute; nh&acirc;n đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ trong năm học 2018-2019.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/7.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Th.s Phạm Văn Nối &ndash; B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường trao giấy khen cho tập thể v&agrave; c&aacute;c c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch trong năm học 2018-2019</em></p>\r\n\r\n<p style=\"text-align:justify\">Để động vi&ecirc;n, kh&iacute;ch lệ tinh thần học tập,đồng thời củng cố th&ecirc;m niềm tin, nu&ocirc;i dưỡng ước mơ, ho&agrave;i b&atilde;o của c&aacute;c em HSSV ngay từ những ng&agrave;y đầu năm học mới, b&agrave; Ng&ocirc; Thị Huệ - Trưởng ph&ograve;ng C&ocirc;ng t&aacute;c HS-SV đ&atilde; c&ocirc;ng bố c&aacute;c quyết định khen thưởng, trao học bổng v&agrave; những phần qu&agrave; &yacute; nghĩa đến c&aacute;c HSSV c&oacute; ho&agrave;n cảnh gia đ&igrave;nh kh&oacute; khăn, c&oacute; cố gắng vươn l&ecirc;n trong học tập.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/8.kg2019.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c học sinh sinh vi&ecirc;n được tặng giấy khen</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/9.kg2019.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c HSSV c&oacute; th&agrave;nh t&iacute;ch học tập xuất sắc được trao học bổng</em></p>\r\n\r\n<p><em><strong>Một số h&igrave;nh ảnh tại lễ khai giảng năm học 2019-2020:</strong></em></p>\r\n\r\n<p><em><strong><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/10.kg2019.jpg\" style=\"width:100%\" /></strong></em></p>\r\n\r\n<p style=\"text-align:center\"><em>Đại diện c&ocirc;ng an phường, hội phụ huynh học sinh ch&uacute;c mừng khai giảng</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/11.kg2019.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>Đại diện c&aacute;c Trung t&acirc;m li&ecirc;n kết ch&uacute;c mừng khai giảng</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/12.kg2019.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c tiết mục văn nghệ ch&agrave;o mừng lễ khai giảng</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/13.kg2019.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c tiết mục văn nghệ ch&agrave;o mừng lễ khai giảng</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/images/news/khai_giang_2019-2020/14.kg2019.jpg\" style=\"width:100%\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c tiết mục văn nghệ ch&agrave;o mừng lễ khai giảng</em></p>\r\n\r\n<p style=\"text-align:justify\">Lễ Khai giảng năm học 2019-2020 kết th&uacute;c tốt đẹp trong niềm vui tươi, phấn khởi, kh&ocirc;ng kh&iacute; tươi vui trong ng&agrave;y khai trường sẽ l&agrave; động lực cho thầy v&agrave; tr&ograve; nh&agrave; trường ho&agrave;n th&agrave;nh tốt c&aacute;c nhiệm vụ được giao dưới m&aacute;i nh&agrave; chung Trường Cao đẳng Kinh tế v&agrave; CNTP.</p>', 50, 1, 1, 1, '2019-09-03 17:00:00', '2021-07-29 09:12:11'),
(11, 'Chương trình và tài liệu 6 môn học chung', 'chuong-trinh-va-tai-lieu-6-mon-hoc-chung-1622736687', 'Các môn học chung là chương trình 6 môn học: Giáo dục chính trị; Pháp luật; Tin học; Ngoại ngữ; Giáo dục thể chất...', 'uploads/images/thumbnail_resize/reading-challenge.jpg', '<p>C&aacute;c m&ocirc;n học chung l&agrave; chương tr&igrave;nh 6 m&ocirc;n học: Gi&aacute;o dục ch&iacute;nh trị; Ph&aacute;p luật; Tin học; Ngoại ngữ; Gi&aacute;o dục thể chất v&agrave; Gi&aacute;o dục quốc ph&ograve;ng an ninh được sử dụng chung, thống nhất trong đ&agrave;o tạo ở tr&igrave;nh độ trung cấp, cao đẳng của hệ thống GDNN.</p>\r\n\r\n<p>Chương tr&igrave;nh 6 m&ocirc;n học n&agrave;y đ&atilde; được Bộ Lao động &ndash; Thương binh v&agrave; X&atilde; hội x&acirc;y dựng v&agrave; ban h&agrave;nh theo c&aacute;c Th&ocirc;ng tư sau:</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 03/2019/TT-BLĐTBXH ng&agrave;y 17/01/2019 Ban h&agrave;nh Chương tr&igrave;nh m&ocirc;n học Tiếng Anh;</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 24/2018/TT-BLĐTBXH ng&agrave;y 06/12/2018 Ban h&agrave;nh Chương tr&igrave;nh m&ocirc;n học Gi&aacute;o dục ch&iacute;nh trị;</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 13/2018/TT-BLĐTBXH ng&agrave;y 26/09/2018 Ban h&agrave;nh Chương tr&igrave;nh m&ocirc;n học Ph&aacute;p luật;</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 12/2018/TT-BLĐTBXH ng&agrave;y 26/09/2018 Ban h&agrave;nh Chương tr&igrave;nh m&ocirc;n học Gi&aacute;o dục thể chất;</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 11/2018/TT-BLĐTBXH ng&agrave;y 26/09/2018 Ban h&agrave;nh Chương tr&igrave;nh m&ocirc;n học Tin học;</p>\r\n\r\n<p>&ndash; Th&ocirc;ng tư số 10/2018/TT-BLĐTBXH ng&agrave;y 26/09/2018 Quy định Chương tr&igrave;nh, tổ chức dạy học v&agrave; đ&aacute;nh gi&aacute; kết quả học tập m&ocirc;n học Gi&aacute;o dục quốc ph&ograve;ng v&agrave; an ninh.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<th>TT</th>\r\n			<th>Chương tr&igrave;nh/T&agrave;i liệu</th>\r\n			<th>Link</th>\r\n		</tr>\r\n		<tr>\r\n			<td>1</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Tiếng Anh (bao gồm file listening)</td>\r\n			<td><a href=\"https://drive.google.com/file/d/13myTWpOj_gjTVqj-1whViUXxPWFudcqs/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>2</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Gi&aacute;o dục ch&iacute;nh trị</td>\r\n			<td><a href=\"https://drive.google.com/file/d/1tg23JpJ28of_ufPZTLhJ9poUXVYJv7UN/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>3</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Ph&aacute;p luật</td>\r\n			<td><a href=\"https://drive.google.com/file/d/1VDEvyWVr6PbR5ZviKqiOb0_SoJWAPs9T/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>4</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Gi&aacute;o dục thể chất</td>\r\n			<td><a href=\"https://drive.google.com/file/d/1cAaxkTnKp0Gbre99TIO9sKCLGLEJorJh/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>5</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Tin học</td>\r\n			<td><a href=\"https://drive.google.com/file/d/1j2K0hycWVO-6aVL0mkj-1C4qLObyYLqy/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>6</td>\r\n			<td>T&agrave;i liệu m&ocirc;n học Gi&aacute;o dục quốc ph&ograve;ng v&agrave; an ninh</td>\r\n			<td><a href=\"https://drive.google.com/file/d/1wMu4cDV6sTCTYC33xBfGjsQWr0lXuJVu/view?usp=sharing\">Tải về</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Hiện đ&atilde; c&oacute; 2 chương tr&igrave;nh m&ocirc;n học chung Tiếng Anh v&agrave; Tin học đ&atilde; được tin học h&oacute;a trở th&agrave;nh c&aacute;c m&ocirc;n học để đ&agrave;o tạo trực tuyến. Xem th&ecirc;m tại địa chỉ https://mhctructuyen.gdnn.gov.vn/</p>', 145, 2, 1, 2, '2021-06-03 09:01:51', '2021-06-03 09:11:27'),
(12, 'Tiếng Anh chuyên ngành', 'tieng-anh-chuyen-nganh-1622830080', 'Nhằm đẩy mạnh dạy và học ngoại ngữ trong giáo dục nghề nghiệp, đặc biệt đối với các ngành, nghề trọng...', 'uploads/images/thumbnail_resize/Tieng_anh_chuyen_nganh.jpg', '<p><a data-src=\"https://daotaocq.gdnn.gov.vn/wp-content/plugins/ari-fancy-lightbox/assets/pdfjs/web/viewer.html?file=https%3A%2F%2Fdaotaocq.gdnn.gov.vn%2Fwp-content%2Fuploads%2F2021%2F04%2F1164-TCGDNN-DTCQ.pdf\" href=\"https://daotaocq.gdnn.gov.vn/wp-content/uploads/2021/04/1164-TCGDNN-DTCQ.pdf\">C&ocirc;ng văn số 1164/TCGDNN-ĐTCQ về việc hướng dẫn &aacute;p chụng chương tr&igrave;nh tiếng Anh chuy&ecirc;n ng&agrave;nh</a></p>\r\n\r\n<p>Nhằm đẩy mạnh dạy v&agrave; học ngoại ngữ trong gi&aacute;o dục nghề nghiệp, đặc biệt đối với c&aacute;c ng&agrave;nh, nghề trọng điểm quốc gia, khu vực v&agrave; quốc tế; thực hiện Đề &aacute;n Dạy v&agrave; học ngoại ngữ trong hệ thống gi&aacute;o dục quốc d&acirc;n giai đoạn 2008 &ndash; 2020&Prime; theo Quyết định số 1400/QĐ-TTG ng&agrave;y 30/9/2008 của Thủ tướng Ch&iacute;nh phủ, trong năm 2019, Bộ Lao động &ndash; Thương binh v&agrave; X&atilde; hội đ&atilde; x&acirc;y dựng v&agrave; ban h&agrave;nh 12 chương tr&igrave;nh tiếng Anh chuy&ecirc;n ng&agrave;nh (English for Special Purposes) ở tr&igrave;nh độ bậc 3 v&agrave; bậc 4 cho c&aacute;c ng&agrave;nh, nghề gồm:</p>\r\n\r\n<p>&ndash; C&ocirc;ng nghệ sinh học;</p>\r\n\r\n<p>&ndash; Hướng dẫn du lịch;</p>\r\n\r\n<p>&ndash; Quản trị lữ h&agrave;nh;</p>\r\n\r\n<p>&ndash; Quản trị nh&agrave; h&agrave;ng;</p>\r\n\r\n<p>&ndash; Quản trị khu Resort;</p>\r\n\r\n<p>&ndash; Quản trị mạng m&aacute;y t&iacute;nh;</p>\r\n\r\n<p>&ndash; C&ocirc;ng nghệ th&ocirc;ng tin (ứng dụng phần mềm);</p>\r\n\r\n<p>&ndash; Thiết kế đồ họa;</p>\r\n\r\n<p>&ndash; Cơ điện tử;</p>\r\n\r\n<p>&ndash; Kỹ thuật lắp đặt điện v&agrave; điều khiển trong c&ocirc;ng nghiệp;</p>\r\n\r\n<p>&ndash; Điện tử c&ocirc;ng nghiệp;</p>\r\n\r\n<p>&ndash; Kỹ thuật m&aacute;y lạnh v&agrave; điều h&ograve;a kh&ocirc;ng kh&iacute;.</p>\r\n\r\n<p>Tr&ecirc;n cơ sở c&aacute;c chương tr&igrave;nh tiếng Anh chuy&ecirc;n ng&agrave;nh đ&atilde; ban h&agrave;nh, c&aacute;c cơ sở gi&aacute;o dục nghề nghiệp, đặc biệt l&agrave; c&aacute;c trường được lựa chọn quy hoạch đầu tư để trở th&agrave;nh trường nghề chất lượng cao c&oacute; thể &aacute;p dụng, x&acirc;y dựng chương tr&igrave;nh chi tiết, tổ chức giảng dạy tăng cường tiếng Anh chuy&ecirc;n ng&agrave;nh cho c&aacute;c sinh vi&ecirc;n tr&igrave;nh độ cao đẳng theo học c&aacute;c ng&agrave;nh, nghề n&ecirc;u tr&ecirc;n đ&aacute;p ứng y&ecirc;u cầu hội nhập khu vực v&agrave; quốc tế.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<th>TT</th>\r\n			<th>Ng&agrave;nh, nghề</th>\r\n			<th>Link</th>\r\n		</tr>\r\n		<tr>\r\n			<td>1</td>\r\n			<td>Quản trị mạng m&aacute;y t&iacute;nh</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1p_8nSwm2e4qy5Um70Y7K5zizT5souLmF\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>2</td>\r\n			<td>C&ocirc;ng nghệ th&ocirc;ng tin (ứng dụng phần mềm)</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1xDR5MwhkivoXosEP4hZQPzRWcMH_E6sp\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>3</td>\r\n			<td>Thiết kế đồ họa</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1cIUWjPv5zqpLsrKYaSJJbhJYoOAOtFGt\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>4</td>\r\n			<td>Cơ điện tử</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1hsOqqtqhol1wc2EGCl21eue0whmXVyVt\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>5</td>\r\n			<td>Kỹ thuật lắp đặt điện v&agrave; điều khiển trong c&ocirc;ng nghiệp</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1Hv-no01-DhgJvBdD3CI2IAQPuo8VEGhP\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>6</td>\r\n			<td>Điện tử c&ocirc;ng nghiệp</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=18l6qKXq8XO-FQwJmytYsNiqHy0CsB-dU\">Tải về</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>7</td>\r\n			<td>Kỹ thuật m&aacute;y lạnh v&agrave; điều h&ograve;a kh&ocirc;ng kh&iacute;</td>\r\n			<td><a href=\"https://drive.google.com/uc?export=download&amp;id=1a-M-6F9YfDTuKFllklAn4YNgeSkmHhJE\">Tải về</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 60, 2, 1, 1, '2021-06-04 11:07:18', '2021-06-04 11:08:00'),
(13, 'Thiết kế nội dung bài dạy theo mẫu giáo án tích hợp trong giáo dục nghề nghiệp', 'thiet-ke-noi-dung-bai-day-theo-mau-giao-an-tich-hop-trong-giao-duc-nghe-nghiep-1628089736', 'Tháng 11 năm 2015, Tổng cục Dạy nghề đã tổ chức tập huấn cho các khoa sư phạm dạy nghề trên phạm vi toàn quốc về...', 'uploads/images/thumbnail_resize/thiet_ke_noi_dung_bai_day_tich_hop.jpg', '<p><strong>I. Đặt vấn đề</strong><br />\r\nTh&aacute;ng 11 năm 2015, Tổng cục Dạy nghề đ&atilde; tổ chức tập huấn cho c&aacute;c khoa sư phạm dạy nghề tr&ecirc;n phạm vi to&agrave;n quốc về &ldquo;Bi&ecirc;n soạn gi&aacute;o &aacute;n v&agrave; tổ chức dạy học t&iacute;ch hợp&rdquo; theo t&agrave;i liệu mới. So với c&aacute;c t&agrave;i liệu hướng dẫn về dạy học t&iacute;ch hợp trước đ&acirc;y, t&agrave;i liệu tập huấn năm 2015 c&oacute; một số nội dung đ&atilde; được thay đổi một c&aacute;ch cơ bản.<br />\r\nDo vậy, việc cụ thể h&oacute;a những nội dung mới trong t&agrave;i liệu &ldquo;Bi&ecirc;n soạn gi&aacute;o &aacute;n v&agrave; tổ chức dạy học t&iacute;ch hợp - 2015&rdquo; &nbsp;đề cập ở tr&ecirc;n nhằm gi&uacute;p cho đội ngũ giảng vi&ecirc;n, gi&aacute;o vi&ecirc;n dạy nghề thuận lợi trong qu&aacute; tr&igrave;nh chuẩn bị v&agrave; tổ chức giảng dạy c&aacute;c b&agrave;i dạy nghề t&iacute;ch hợp, t&aacute;c giả bi&ecirc;n soạn b&agrave;i viết &ldquo;Thiết kế nội dung b&agrave;i dạy theo mẫu chung gi&aacute;o &aacute;n dạy học t&iacute;ch hợp trong gi&aacute;o dục nghề nghiệp&rdquo;<br />\r\n<strong>II. Giải quyết vấn đề</strong><br />\r\n<strong>1. Một số kh&aacute;i niệm</strong><br />\r\n<em>1.1. Dạy học t&iacute;ch hợp</em><br />\r\nTrong lĩnh vực Gi&aacute;o dục nghề nghiệp, do đặc điểm l&agrave; đ&agrave;o tạo nghề đ&aacute;p ứng năng lực thực hiện (NLTH), dạy học t&iacute;ch hợp l&agrave; vừa dạy nội dung l&yacute; thuyết v&agrave; thực h&agrave;nh trong c&ugrave;ng một b&agrave;i dạy, tr&ecirc;n cơ sở nội dung chương tr&igrave;nh được thiết kế theo m&ocirc; đun theo định hướng ph&aacute;t triển năng lực, nội dung từng b&agrave;i cũng được thiết kế sao cho c&oacute; thể t&iacute;ch hợp dạy kiến thức l&yacute; thuyết v&agrave; tiến h&agrave;nh thực h&agrave;nh sao cho người học được trang bị năng lực đ&aacute;p ứng mục ti&ecirc;u dạy học.<br />\r\n<em>1.2. B&agrave;i dạy t&iacute;ch hợp</em><br />\r\nB&agrave;i dạy t&iacute;ch hợp cũng c&oacute; thể c&oacute; nhiều c&aacute;ch ph&aacute;t biểu kh&aacute;c nhau. Trong lĩnh vực Gi&aacute;o dục nghề nghiệp c&oacute; thể hiểu: B&agrave;i dạy t&iacute;ch hợp l&agrave; đơn vị học tập nhỏ nhất c&oacute; khả năng h&igrave;nh th&agrave;nh nơi người học cả kiến thức, kỹ năng, th&aacute;i độ cần thiết để giải quyết một c&ocirc;ng việc hoặc một phần c&ocirc;ng việc chuy&ecirc;n m&ocirc;n cụ thể, g&oacute;p phần h&igrave;nh th&agrave;nh năng lực thực hiện hoạt động nghề nghiệp của họ.<br />\r\n<em>1.3. Nội dung b&agrave;i dạy t&iacute;ch hợp</em><br />\r\nTrong Gi&aacute;o dục nghề nghiệp, nội dung b&agrave;i dạy t&iacute;ch hợp l&agrave; kiến thức, kỹ năng v&agrave; th&aacute;i độ được x&aacute;c định từ mục ti&ecirc;u học tập của b&agrave;i dạy t&iacute;ch hợp nhằm h&igrave;nh th&agrave;nh cho người học năng lực thực hiện một c&ocirc;ng việc hay một phần c&ocirc;ng việc của nghề.<br />\r\nNội dung b&agrave;i dạy t&iacute;ch hợp phải được xử l&yacute; v&agrave; cấu tr&uacute;c theo tiến tr&igrave;nh dạy học t&iacute;ch hợp được thể hiện trong quan điểm dạy học &aacute;n t&iacute;ch hợp chứ kh&ocirc;ng phải sao ch&eacute;p một c&aacute;ch cơ học từ gi&aacute;o tr&igrave;nh, t&agrave;i liệu.<br />\r\n<em>1.4. X&acirc;y dựng/ thiết kế nội dung b&agrave;i dạy t&iacute;ch hợp</em><br />\r\nX&acirc;y dựng/ thiết kế nội dung học tập ch&iacute;nh l&agrave; x&acirc;y dựng đề cương của b&agrave;i dạy, l&agrave; nhiệm vụ quan trọng của qu&aacute; tr&igrave;nh chuẩn bị dạy học. Bao gồm: 1)X&aacute;c định/ ph&acirc;n t&iacute;ch mục ti&ecirc;u b&agrave;i dạy; 2) X&aacute;c định c&aacute;c năng lực th&agrave;nh tố trong b&agrave;i dạy; 3) X&aacute;c định kiến thức li&ecirc;n quan của c&aacute;c năng lực th&agrave;nh tố; 4) X&aacute;c định tr&igrave;nh tự thực hiện c&aacute;c năng lực th&agrave;nh tố.<br />\r\n<em>1.5. Năng lực th&agrave;nh tố</em><br />\r\nNăng lực th&agrave;nh tố l&agrave; những phần hợp th&agrave;nh năng lực, l&agrave; khả năng thực hiện được c&aacute;c c&ocirc;ng việc hoặc phần c&ocirc;ng việc của nghề theo c&aacute;c ti&ecirc;u chuẩn đặt ra.<br />\r\nMột năng lực th&agrave;nh tố gắn với một t&igrave;nh huống nghề nghiệp v&agrave; được thực hiện th&ocirc;ng qua một quy tr&igrave;nh nhất định, kết quả sẽ tạo ra một sản phẩm cụ thể hoặc một phần sản phẩm.<br />\r\n<strong>2. C&aacute;c quan điểm về dạy học t&iacute;ch hợp</strong><br />\r\nTheo y&ecirc;u cầu của từng bộ m&ocirc;n, người ta ph&acirc;n ra l&agrave;m 4 loại:<br />\r\nQuan điểm &ldquo;trong nội bộ m&ocirc;n học&rdquo; (t&iacute;ch hợp trong m&ocirc;n học): Ưu ti&ecirc;n c&aacute;c nội dung của m&ocirc;n học. Quan điểm n&agrave;y duy tr&igrave; c&aacute;c m&ocirc;n học ri&ecirc;ng rẽ.<br />\r\nQuan điểm &ldquo;đa m&ocirc;n&rdquo;: trong đ&oacute; &nbsp;đề nghị những t&igrave;nh huống, những đề t&agrave;i c&oacute; thể được nghi&ecirc;n cứu theo những &nbsp;quan điểm kh&aacute;c nhau, nghĩa l&agrave; theo những m&ocirc;n học kh&aacute;c nhau. Theo quan điểm n&agrave;y những m&ocirc;n học tiếp tục được tiếp cận một c&aacute;ch ri&ecirc;ng rẽ v&agrave; chỉ gặp nhau ở một số thời điểm trong qu&aacute; &nbsp;tr&igrave;nh nghi&ecirc;n cứu. Như vậy m&ocirc;n học chưa thực sự được t&iacute;ch hợp.<br />\r\nQuan điểm &ldquo;li&ecirc;n m&ocirc;n&rdquo;: trong đ&oacute; đề xuất những t&igrave;nh huống chỉ c&oacute; thể tiếp cận một c&aacute;ch hợp l&yacute; qua sự soi s&aacute;ng của nhiều m&ocirc;n học. Ở đ&acirc;y nhấn mạnh đến sự li&ecirc;n kết của nhiều m&ocirc;n l&agrave;m cho ch&uacute;ng t&iacute;ch hợp với nhau để giải quyết một t&igrave;nh huống cho trước. Qu&aacute; tr&igrave;nh học tập sẽ kh&ocirc;ng bị rời rạc m&agrave; phải li&ecirc;n kết với xung quanh những vấn đề cần giải quyết.<br />\r\nQuan điểm &ldquo;xuy&ecirc;n m&ocirc;n&rdquo;: chủ yếu ph&aacute;t triển kỹ năng m&agrave; HS c&oacute; thể sử dụng trong tất cả c&aacute;c m&ocirc;n học, trong tất cả c&aacute;c t&igrave;nh huống. Những kỹ năng n&agrave;y gọi l&agrave; kỹ năng &nbsp;xuy&ecirc;n m&ocirc;n. C&oacute; thể lĩnh hội c&aacute;c kỹ năng n&agrave;y trong từng m&ocirc;n học hoặc nh&acirc;n dịp c&oacute; những hoạt động chung cho nhiều m&ocirc;n học.<br />\r\nHiện nay, trong Gi&aacute;o dục nghề nghiệp tiếp cận năng lực thực hiện nhấn mạnh quan điểm &ldquo;T&iacute;ch hợp dạy l&yacute; thuyết với thực h&agrave;nh&rdquo; trong c&ugrave;ng một b&agrave;i học.<br />\r\n<strong>3. Nguy&ecirc;n tắc thực hiện dạy học t&iacute;ch hợp</strong><br />\r\nDạy học theo quan điểm t&iacute;ch hợp phải coi mỗi b&agrave;i dạy l&agrave; một chỉnh thể thống nhất, trọn vẹn v&agrave; ho&agrave;n chỉnh về nội dung nhằm từng bước thực hiện c&aacute;c mục ti&ecirc;u của m&ocirc;n học;<br />\r\nQuan điểm t&iacute;ch hợp phải được qu&aacute;n triệt từ kh&acirc;u x&aacute;c định mục ti&ecirc;u, nội dung chương tr&igrave;nh m&ocirc;n học đến kh&acirc;u cấu tr&uacute;c b&agrave;i dạy, lựa chọn PPDH v&agrave; c&aacute;c h&igrave;nh thức tổ chức dạy học để thiết lập c&aacute;c t&igrave;nh huống dạy học gi&uacute;p HS vừa củng cố vừa vận dụng tổng hợp c&aacute;c kiến thức đ&atilde; học ở c&aacute;c bộ m&ocirc;n.<br />\r\nDạy học theo quan điểm t&iacute;ch hợp phải c&oacute; cấu tr&uacute;c mềm dẻo, linh hoạt, tạo được sự lien th&ocirc;ng giữa c&aacute;c loại h&igrave;nh đ&agrave;o tạo.<br />\r\n<strong>4. Thiết kế nội dung b&agrave;i dạy t&iacute;ch hợp</strong><br />\r\nCăn cứ v&agrave;o quan điểm, nguy&ecirc;n tắc dạy học t&iacute;ch hợp v&agrave; mẫu gi&aacute;o &aacute;n t&iacute;ch hợp, c&aacute;c nội dung b&agrave;i dạy t&iacute;ch hợp được x&aacute;c định v&agrave; cấu tr&uacute;c theo tiến tr&igrave;nh dạy học t&iacute;ch hợp theo tr&igrave;nh tự sau:</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/thiet%20ke%20noi%20dung%20bai%20day%20tich%20hop.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Thiết kế nội dung b&agrave;i dạy theo mẫu gi&aacute;o &aacute;n t&iacute;ch hợp trong gi&aacute;o dục nghề nghiệp</em></p>\r\n\r\n<p><strong>Bước 1: X&aacute;c định/ ph&acirc;n t&iacute;ch mục ti&ecirc;u b&agrave;i dạy</strong><br />\r\nCăn cứ chương tr&igrave;nh đ&agrave;o tạo v&agrave; chủ đề b&agrave;i dạy để x&aacute;c định c&aacute;c mục ti&ecirc;u kiến thức, mục ti&ecirc;u kỹ năng, mục ti&ecirc;u th&aacute;i độ đối với người học.<br />\r\nNhững lưu &yacute; khi viết mục ti&ecirc;u b&agrave;i dạy:<br />\r\n1) Mục ti&ecirc;u phải bắt đầu bằng 1 động từ chỉ h&agrave;nh động. Tr&aacute;nh sử dụng c&aacute;c từ chỉ trạng th&aacute;i, như: &ldquo;hiểu&rdquo;, &ldquo;nắm&rdquo;, &ldquo;biết&rdquo;, &ldquo;c&oacute;&rdquo; khi viết mục ti&ecirc;u.<br />\r\n2) Mục ti&ecirc;u phải c&oacute; ti&ecirc;u ch&iacute; để đo (ti&ecirc;u ch&iacute; về kỹ thuật, mỹ thuật, sự an to&agrave;n, v&agrave; thời gian thực hiện...)<br />\r\n3) Mục ti&ecirc;u phải ph&acirc;n định r&otilde; mức độ nắm vững kiến thức, kỹ năng, th&aacute;i độ (dựa v&agrave;o c&aacute;c mức độ mục ti&ecirc;u nhận thức, c&aacute;c mức độ mục ti&ecirc;u kỹ năng, c&aacute;c mức độ mục ti&ecirc;u th&aacute;i độ)<br />\r\n<strong>Bước 2: X&aacute;c định c&aacute;c năng lực th&agrave;nh tố của b&agrave;i dạy</strong><br />\r\nDựa v&agrave;o mục ti&ecirc;u của b&agrave;i, chương tr&igrave;nh m&ocirc; đun v&agrave; thực tiễn nghề nghiệp để x&aacute;c định c&aacute;c năng lực th&agrave;nh tố của b&agrave;i dạy;<br />\r\nMột năng lực th&agrave;nh tố gắn với 01 quy tr&igrave;nh thực hiện, kết quả sẽ tạo ra một sản phẩm cụ thể hoặc một phần sản phẩm.<br />\r\n<strong>V&iacute; dụ 1:</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong>T&ecirc;n b&agrave;i: &nbsp;THAY BĂNG VẾT THƯƠNG THƯỜNG CHO BỆNH NH&Acirc;N</strong></p>\r\n\r\n<p><strong>Mục ti&ecirc;u b&agrave;i học:</strong><br />\r\n<em>Sau khi học xong b&agrave;i n&agrave;y người học c&oacute; khả năng:</em><br />\r\n- Ph&acirc;n t&iacute;ch được mục đ&iacute;ch, nguy&ecirc;n tắc thay băng vết thường cho bệnh nh&acirc;n.<br />\r\n- Thay băng vết thương thường cho bệnh nh&acirc;n đ&uacute;ng quy tr&igrave;nh, đảm bảo c&aacute;c nguy&ecirc;n tắc quy định của ng&agrave;nh.<br />\r\n- Thực hiện c&ocirc;ng việc với th&aacute;i độ &acirc;n cần, cảm th&ocirc;ng với bệnh nh&acirc;n.<br />\r\n<em>=&gt; Soạn gi&aacute;o &aacute;n dạy năng lực thực hiện một c&ocirc;ng việc: Thay băng vết thương thường cho bệnh nh&acirc;n.</em><br />\r\n<strong>V&iacute; dụ 2:</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong>T&ecirc;n b&agrave;i: THAY LỐP XE &Ocirc; T&Ocirc; KH&Ocirc;NG SĂM</strong></p>\r\n\r\n<p><strong>Mục ti&ecirc;u b&agrave;i học:</strong><br />\r\n<em>Sau khi học xong b&agrave;i n&agrave;y người học c&oacute; khả năng:</em><br />\r\n- Tr&igrave;nh b&agrave;y được cấu tạo v&agrave; c&aacute;c th&ocirc;ng số ghi tr&ecirc;n lốp xe &ocirc; t&ocirc; kh&ocirc;ng săm.<br />\r\n- Th&aacute;o, lắp lốp xe &ocirc; t&ocirc; kh&ocirc;ng săm đ&uacute;ng qui tr&igrave;nh v&agrave; đảm bảo c&aacute;c y&ecirc;u c&acirc;̀u kỹ thu&acirc;̣t sau: v&agrave;nh xe kh&ocirc;ng bị trầy, th&ocirc;ng số ghi tr&ecirc;n lốp mới đ&uacute;ng với th&ocirc;ng số ghi tr&ecirc;n lốp cũ, &aacute;p suất bơm lốp xe đạt 2,3 kg/cm2, b&aacute;nh xe kh&ocirc;ng bị x&igrave; hơi.<br />\r\n- R&egrave;n luyện t&iacute;nh cẩn thận, ch&iacute;nh x&aacute;c, l&agrave;m việc theo quy tr&igrave;nh, bảo đảm an to&agrave;n cho người v&agrave; thiết bị.<br />\r\n<em>=&gt; Soạn gi&aacute;o &aacute;n dạy năng lực thực hiện hai năng lực th&agrave;nh tố:</em><br />\r\nNăng lực th&agrave;nh tố 1: Th&aacute;o lốp xe &ocirc; t&ocirc; kh&ocirc;ng săm<br />\r\nNăng lực th&agrave;nh tố 2: Lắp lốp xe &ocirc; t&ocirc; kh&ocirc;ng săm<br />\r\n<strong>Bước 3: X&aacute;c định c&aacute;c kiến thức li&ecirc;n quan của c&aacute;c năng lực th&agrave;nh tố</strong><br />\r\nChỉ x&aacute;c định những kiến thức vừa đủ, li&ecirc;n quan đến từng năng lực th&agrave;nh tố (dựa v&agrave;o chương tr&igrave;nh, c&aacute;c gi&aacute;o tr&igrave;nh, t&agrave;i liệu chuy&ecirc;n ng&agrave;nh)<br />\r\nM&ocirc; tả chi tiết c&aacute;c kiến thức li&ecirc;n quan bằng ng&ocirc;n từ chuy&ecirc;n ng&agrave;nh, s&uacute;c t&iacute;ch; ch&egrave;n h&igrave;nh vẽ, h&igrave;nh ảnh minh hoạ (nếu c&oacute;)<br />\r\n<strong>Bước 4: X&aacute;c định tr&igrave;nh tự thực hiện c&aacute;c năng lực th&agrave;nh tố</strong><br />\r\nX&aacute;c định c&aacute;c bước thực hiện c&aacute;c năng lực th&agrave;nh tố. Danh mục c&aacute;c bước kh&ocirc;ng n&ecirc;n qu&aacute; ngắn (2-3 bước), hoặc kh&ocirc;ng n&ecirc;n qu&aacute; d&agrave;i (tr&ecirc;n một trang)<br />\r\nSắp xếp c&aacute;c bước thực hiện nối tiếp nhau theo một tr&igrave;nh tự hợp l&yacute;;<br />\r\nM&ocirc; tả c&aacute;c bước, bao gồm: phương ph&aacute;p thực hiện, ti&ecirc;u chuẩn &nbsp;thực hiện, x&aacute;c định c&aacute;c dụng cụ, thiết bị v&agrave; phương tiện, c&aacute;c vấn đề an to&agrave;n khi thực hiện c&aacute;c bước;<br />\r\nX&aacute;c định c&aacute;c sai phạm thường gặp, nguy&ecirc;n nh&acirc;n, c&aacute;ch ph&ograve;ng v&agrave; khắc phục khi thực hiện c&aacute;c năng lực th&agrave;nh tố.<br />\r\nBước 5: X&aacute;c định nhiệm vụ thực h&agrave;nh/luyện tập<br />\r\nCăn cứ mục ti&ecirc;u của b&agrave;i dạy, điều kiện về cơ sở vật chất v&agrave; kinh ph&iacute; học tập gi&aacute;o vi&ecirc;n x&aacute;c định nhiệm vụ thực h&agrave;nh cho người học. Bao gồm c&aacute;c nội dung sau:<br />\r\n- X&aacute;c định nhiệm vụ/ t&igrave;nh huống thực h&agrave;nh đối với c&aacute; nh&acirc;n hoặc nh&oacute;m;<br />\r\n- X&aacute;c định c&aacute;c y&ecirc;u cầu về an to&agrave;n, vệ sinh,...đối khi thực h&agrave;nh;<br />\r\n- X&aacute;c định thời gian thực hiện.<br />\r\n<strong>5. Định dạng nội dung b&agrave;i dạy t&iacute;ch hợp</strong></p>\r\n\r\n<p><strong>T&ecirc;n b&agrave;i:&nbsp;</strong>....................................................................</p>\r\n\r\n<p><strong>Mục ti&ecirc;u b&agrave;i học:</strong><br />\r\n<em>Sau khi học xong b&agrave;i n&agrave;y người học c&oacute; khả năng:</em><br />\r\n................................................................................................................................<br />\r\n<strong>NỘI DUNG HỌC TẬP:</strong><br />\r\n<strong>1. Năng lực th&agrave;nh tố 1</strong><br />\r\n<strong>a) L&yacute; thuyết li&ecirc;n quan</strong>&nbsp;(chỉ dạy những kiến thức l&yacute; thuyết vừa đủ, li&ecirc;n quan đến năng lực th&agrave;nh tố 1)<br />\r\n&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..................&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;................<br />\r\n<strong>b) Tr&igrave;nh tự thực hiện</strong>&nbsp;(hướng dẫn ban đầu thực hiện năng lực th&agrave;nh tố 1)<br />\r\nBước 1: &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..<br />\r\nBước 2: &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..<br />\r\nBước n: &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..<br />\r\n* Sai phạm thường gặp, nguy&ecirc;n nh&acirc;n v&agrave; c&aacute;ch ph&ograve;ng tr&aacute;nh, khắc phục<br />\r\n<strong>c) Thực h&agrave;nh</strong>&nbsp;(hướng dẫn thường xuy&ecirc;n thực hiện năng lực th&agrave;nh tố 1)<br />\r\n- Nhiệm vụ/ t&igrave;nh huống:..........................................................<br />\r\n- Y&ecirc;u cầu về an to&agrave;n, vệ sinh,.................................................<br />\r\n- Thời gian:..............................................................................</p>\r\n\r\n<p><strong>PHIẾU HƯỚNG DẪN&nbsp;LUYỆN TẬP</strong></p>\r\n\r\n<p>T&ecirc;n kỹ năng:&nbsp;...........................................................................</p>\r\n\r\n<p>Họ v&agrave; t&ecirc;n học sinh: &hellip;&hellip;&hellip;&hellip;&hellip;....................Ca&nbsp;&hellip;..&hellip;&hellip;Lớp:...&hellip;&hellip;...</p>\r\n\r\n<p>Thời gian: ................... ......................................... Địa điểm:.................</p>\r\n\r\n<p>Tr&igrave;nh tự thực hiện:</p>\r\n\r\n<table border=\"1\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>T&ecirc;n bước</p>\r\n\r\n			<p>thực hiện</p>\r\n			</td>\r\n			<td>\r\n			<p>Hướng dẫn thực hiện</p>\r\n			</td>\r\n			<td>\r\n			<p>Ti&ecirc;u chuẩn thực hiện</p>\r\n			</td>\r\n			<td>\r\n			<p>Lưu &yacute; khi thực hi&ecirc;̣n</p>\r\n			</td>\r\n			<td>\r\n			<p>Thời gian</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>...</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>........</p>\r\n			</td>\r\n			<td>\r\n			<p>...</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>n. Năng lực th&agrave;nh tố n</strong><br />\r\n<em>(C&aacute;c phần tương tự như thực hiện năng lực th&agrave;nh tố 1)</em><br />\r\n<strong>6. Kết luận</strong><br />\r\nC&aacute;c chương tr&igrave;nh dạy nghề trong hệ thống gi&aacute;o dục nghề nghiệp được thiết kế theo quan điểm kết hợp m&ocirc;n học v&agrave; m&ocirc; đun kỹ năng h&agrave;nh nghề. C&aacute;c m&ocirc; đun được x&acirc;y dựng theo quan điểm hướng đến năng lực thực hiện, t&iacute;ch hợp giữa l&yacute; thuyết v&agrave; thực h&agrave;nh để người học sau khi học xong c&oacute; năng lực thực hiện được c&ocirc;ng việc cụ thể của nghề nghiệp.<br />\r\nXuất ph&aacute;t từ y&ecirc;u cầu thực tiễn về tổ chức thực hiện c&aacute;c b&agrave;i học trong c&aacute;c m&ocirc; đun đ&agrave;o tạo nghề v&agrave; nhu cầu cần cập nhật những kiến thức về dạy học t&iacute;ch hợp đối với gi&aacute;o vi&ecirc;n của c&aacute;c cở sở gi&aacute;o dục nghề nghiệp, nội dung b&agrave;i viết đ&atilde; hệ thống v&agrave; ph&acirc;n t&iacute;ch những kh&aacute;i niệm li&ecirc;n quan đến dạy học t&iacute;ch hợp, ph&acirc;n t&iacute;ch quy tr&igrave;nh x&acirc;y dựng đề cương b&agrave;i dạy theo mẫu chung gi&aacute;o &aacute;n t&iacute;ch hợp để dựa v&agrave;o cơ sở đ&oacute;, gi&aacute;o vi&ecirc;n c&oacute; thể chuẩn bị, tổ chức thực hiện c&aacute;c b&agrave;i dạy t&iacute;ch hợp trong c&aacute;c chương tr&igrave;nh m&ocirc; đun đ&agrave;o tạo nghề đảm bảo t&iacute;nh khoa học v&agrave; c&aacute;c y&ecirc;u cầu về sư phạm kỹ thuật.</p>\r\n\r\n<p><strong>T&agrave;i liệu tham khảo:</strong><br />\r\n1. Quyết định số 62/2008/QĐ-BLĐTBXH, mẫu gi&aacute;o &aacute;n t&iacute;ch hợp.<br />\r\n2. Tổng cục Dạy nghề (2015), t&agrave;i liệu bồi dưỡng về bi&ecirc;n soạn gi&aacute;o &aacute;n v&agrave; tổ chức dạy học t&iacute;ch hợp.<br />\r\n3. Nguyễn Văn Tuấn(2007), Phương ph&aacute;p dạy học kỹ thuật chuy&ecirc;n ng&agrave;nh, NXB ĐH SPKT Tp.HCM.</p>\r\n\r\n<p style=\"text-align:right\"><strong>T&aacute;c giả b&agrave;i viết:&nbsp;</strong>ThS. Ho&agrave;ng Thiếu Sơn - Trường CĐN TNDT T&acirc;y Nguy&ecirc;n</p>', 84, 2, 1, 1, '2019-11-07 17:00:00', '2021-08-04 15:08:56');
INSERT INTO `posts` (`id`, `title`, `slug`, `introtext`, `thumbnail`, `content`, `views`, `category_id`, `user_id`, `group`, `created_at`, `updated_at`) VALUES
(14, 'Phân tích cấu trúc bài giảng tích hợp', 'phan-tich-cau-truc-bai-giang-tich-hop-1627504180', 'Quan điểm đổi mới chất lượng dạy học trong dạy nghề là trang bị cho học sinh các năng lực thực hiện nhiều hơn...', 'uploads/images/thumbnail_resize/nang-luc-thuc-hien.jpeg', '<p><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong>1. Cơ sở khoa học</strong><br />\r\n<strong>1.1. Quan điểm dạy học định hướng hoạt động</strong><br />\r\nQuan điểm đổi mới chất lượng dạy học trong dạy nghề l&agrave; trang bị cho học sinh c&aacute;c năng lực thực hiện nhiều hơn những tri thức c&oacute; t&iacute;nh t&aacute;i hiện lại. Để thực hiện được định hướng đổi mới n&agrave;y phải cần đến c&aacute;c phương thức đ&agrave;o tạo c&oacute; t&iacute;nh hoạt động v&agrave; c&oacute; t&iacute;nh giải quyết vấn đề. Người học cần được trang bị một lượng tri thức cơ bản đồng thời li&ecirc;n kết v&agrave; định hướng tới c&aacute;c năng lực. Một vấn đề đặt ra ở đ&acirc;y l&agrave; phương ph&aacute;p dạy v&agrave; học n&agrave;o l&agrave; mang lại hiệu quả h&igrave;nh th&agrave;nh được ở học sinh c&aacute;c năng lực. Đ&atilde; từ l&acirc;u người ta nghi&ecirc;n cứu tiếp cận l&yacute; thuyết hoạt động để thiết kế tổ chức dạy học hướng đến c&aacute;c năng lực tr&ecirc;n. Bản chất của kiểu dạy học n&agrave;y l&agrave; người học phải hoạt động cả tay ch&acirc;n v&agrave; tr&iacute; &oacute;c để tạo ra một sản phẩm hoạt động. Hoạt động học tập n&agrave;y l&agrave; một hoạt động c&oacute; t&iacute;nh trọn vẹn.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><img alt=\"\" src=\"/uploads/images/news/nang-luc-thuc-hien.jpeg\" style=\"width:100%\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"color:#333333\"><strong>1.2. L&yacute; thuyết h&agrave;nh động trong t&acirc;m l&yacute; học nghề nghiệp</strong></span><br />\r\n<span style=\"color:#333333\">Hoạt đ&ocirc;ng nọi chung v&agrave; hoạt động học tập của học sinh c&aacute;o cấu tr&uacute;c sau:</span><br />\r\n<span style=\"color:#333333\"><strong>-&nbsp;</strong>Một hoạt động bao gồm nhiều h&agrave;nh động v&agrave; bao giờ cũng nhằm v&agrave;o đối tượng để chiếm lĩnh n&oacute;. Ch&iacute;nh đối tượng đ&oacute; trở th&agrave;nh động cơ&nbsp; hoạt động của chủ thể;</span><br />\r\n<span style=\"color:#333333\"><strong>-&nbsp;</strong>H&agrave;nh động được thực hiện bằng h&agrave;ng loạt c&aacute;c thao t&aacute;c để giải quyết những nhiệm vụ nhất định, nhằm đạt mục đ&iacute;ch của h&agrave;nh động;</span><br />\r\n<span style=\"color:#333333\"><strong>-&nbsp;</strong>Thao t&aacute;c gắn liền với việc sử dụng c&aacute;c c&ocirc;ng cụ, phương tiện trong những điều kiện cụ thể.</span><br />\r\n<span style=\"color:#333333\">Trong bất kỳ h&agrave;nh động c&oacute; &yacute; thức n&agrave;o, c&aacute;c yếu tố t&acirc;m l&yacute; đều giữ những&nbsp;<em>chức năng</em>:</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><em>Định hướng</em>&nbsp;h&agrave;nh động;</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><em>Th&uacute;c đẩy</em>&nbsp;h&agrave;nh động;</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Điều khiển thực hiện h&agrave;nh động;</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Kiểm tra, điều chỉnh h&agrave;nh động.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"color:#333333\">Vận dụng l&yacute; thuyết hoạt động v&agrave;o hoạt động dạy học tức l&agrave; phải coi&nbsp;<em>học sinh l&agrave; chủ thể</em>&nbsp;của mọi hoạt động học tập (học l&yacute; thuyết, học thực h&agrave;nh, thực tập sản xuất, học c&aacute;c hoạt động văn h&oacute;a, x&atilde; hội...),&nbsp;<em>gi&aacute;o vi&ecirc;n cần phải x&acirc;y dựng n&ecirc;n nội dung hoạt động</em>&nbsp;đ&aacute;p ứng y&ecirc;u cầu của mục ti&ecirc;u đ&agrave;o tạo thể hiện th&agrave;nh hệ thống những nhiệm vụ cụ thể v&agrave;&nbsp;<em>tổ chức hoạt động của học sinh</em>&nbsp;thực sự c&oacute; kết quả.</span><br />\r\n<span style=\"color:#333333\">Bản chất của dạy học định hướng hoạt động l&agrave;&nbsp;<em><u>hướng học sinh v&agrave;o hoạt động giải quyết c&aacute;c vấn đề kỹ thuật hoặc c&aacute;c nhiệm vụ t&igrave;nh huống nghề nghiệp</u></em>, nhằm chuẩn bị cho học sinh tham gia v&agrave;o giải quyết c&aacute;c nhiệm vụ nghề nghiệp.</span><br />\r\n<span style=\"color:#333333\">Trọng t&acirc;m kiểu dạy học định hướng hoạt động l&agrave; tổ chức qu&aacute; tr&igrave;nh dạy học m&agrave; trong đ&oacute; học sinh hoạt động để tạo ra một sản phẩm. Th&ocirc;ng qua đ&oacute; ph&aacute;t triển được c&aacute;c năng lực hoạt động nghề nghiệp. C&aacute;c bản chất cụ thể như sau:</span><br />\r\n<span style=\"color:#333333\">- Dạy học định hướng hoạt động l&agrave; tổ chức học sinh hoạt động mang t&iacute;nh trọn vẹn, m&agrave; trong đ&oacute; học sinh độc lập thiết kế kế hoạch qui tr&igrave;nh hoạt động, thực hiện hoạt động theo kế hoạch v&agrave; kiểm tra đ&aacute;nh gi&aacute; kết quả hoạt động.</span><br />\r\n<span style=\"color:#333333\">- Tổ chức qu&aacute; tr&igrave;nh dạy học, m&agrave; trong đ&oacute; học sinh học th&ocirc;ng qua hoạt động độc lập &iacute;t nhất l&agrave; theo qui tr&igrave;nh c&aacute;ch thức của họ.</span><br />\r\n<span style=\"color:#333333\">- Học qua c&aacute;c hoạt động cụ thể m&agrave; kết quả của hoạt động đ&oacute; kh&ocirc;ng nhất thiết tuyệt đối m&agrave; c&oacute; t&iacute;nh chất l&agrave; mở (c&aacute;c kết quả hoạt động c&oacute; thể kh&aacute;c nhau);</span><br />\r\n<span style=\"color:#333333\">- Tổ chức tiến h&agrave;nh giờ học hướng đến mục ti&ecirc;u h&igrave;nh th&agrave;nh ở học sinh kỹ năng giải quyết nhiệm vụ nghề nghiệp.</span><br />\r\n<span style=\"color:#333333\">- Kết quả b&agrave;i dạy học định hướng hoạt động tạo ra được sản phẩm vật chất hay &yacute; tưởng (phi vật chất). &nbsp;&nbsp;</span><br />\r\n<span style=\"color:#333333\"><strong>1.3. Cơ sở l&yacute; luận dạy học</strong></span><br />\r\n<span style=\"color:#333333\">Về kh&iacute;a cạnh phương ph&aacute;p dạy học. Giờ học theo kiểu định hướng hoạt động được tổ chức theo qui tr&igrave;nh 4 giai đoạn như sau:</span><br />\r\n<span style=\"color:#333333\"><strong><em>1.3.1. Giai đoạn thứ nhất: Đưa ra vấn đề nhiệm vụ b&agrave;i dạy &ndash; Tr&igrave;nh b&agrave;y y&ecirc;u cầu về kết quả học tập (sản phẩm)</em></strong></span><br />\r\n<span style=\"color:#333333\">Ở giai đoạn n&agrave;y, gi&aacute;o vi&ecirc;n đưa ra nhiệm vụ b&agrave;i dạy để học sinh &yacute; thức được sản phẩm hoạt động cần thực hiện trong b&agrave;i dạy v&agrave; y&ecirc;u cầu cần đạt được. H&igrave;nh thức tr&igrave;nh b&agrave;y rất phong ph&uacute; v&agrave; đa dạng, t&ugrave;y thuộc v&agrave;o điều kiện v&agrave; khả năng của gi&aacute; vi&ecirc;n.</span><br />\r\n<span style=\"color:#333333\">Nếu c&oacute; điều kiện th&igrave; tổ chức t&igrave;nh huống học tập (THHT) ngay tại lớp học. Nếu tr&igrave;nh huống qu&aacute; phức tạp th&igrave; tổ chức cho lớp học tiếp cận ngay tại hiện trường (tham quan hoc tập), hoặc ghi h&igrave;nh hiện trường rồi tr&igrave;nh chiếu lại tr&ecirc;n lớp. Nếu kh&ocirc;ng c&oacute; điều kiện th&igrave; đơn giảng chỉ l&agrave; lời kể lại, m&ocirc; tả lại của gi&aacute;o vi&ecirc;n bằng lời, bằng h&igrave;nh vẽ hay tranh ảnh tượng trưng. Việc n&agrave;y kh&ocirc;ng đơn giản chỉ để dẫn nhập m&agrave; c&ograve;n c&oacute; nhiều t&aacute;c động xuy&ecirc;n suốt b&agrave;i dạy. C&oacute; b&agrave;i viết ri&ecirc;ng về THHT tr&igrave;nh b&agrave;y đầy đủ hơn.</span><br />\r\n<span style=\"color:#333333\">Sản phảm hoạt động c&agrave;ng phức tạp th&igrave; th&igrave; độ kh&oacute; đối với học sinh c&agrave;ng lớn. Th&ocirc;ng thường, c&aacute;c b&agrave;i học được bắt đầu với c&aacute;c nhiệm vụ đơn giản. Trong giai đoạn n&agrave;y gi&aacute;o vi&ecirc;n kh&ocirc;ng chỉ giao nhiệm vụ m&agrave; c&ograve;n thống nhất/qu&aacute;n triệt với học sinh về kế hoạch, ph&acirc;n nh&oacute;m v&agrave; cung cấp c&aacute;c th&ocirc;ng tin về t&agrave;i liệu li&ecirc;n quan để học sinh chủ động lĩnh hội trong qu&aacute; tr&igrave;nh thực hiện.</span><br />\r\n<span style=\"color:#333333\"><strong><em>1.3.2. Giai đoạn thứ 2: &nbsp;Tổ chức lập kế hoạch giải quyết vấn đề</em></strong></span><br />\r\n<span style=\"color:#333333\">Trong giai đoạn n&agrave;y, gi&aacute;o vi&ecirc;n tổ chức cho học sinh thu thập th&ocirc;ng tin qua THHT, những g&igrave; quan s&aacute;t được, th&acirc;u lượm được, rồi đối chiếu với điều kiện hiện tại. Từ đ&oacute; x&aacute;c định c&aacute;i g&igrave; mới chưa biết cần phải học, c&aacute;i g&igrave; đ&atilde; biết cần vận dụng c&aacute;i n&agrave;o kh&oacute; cần phải hỏi... Như vậy ta thấy THHT đ&oacute;ng vai tr&ograve; hết sức quan trọng, cho n&ecirc;n x&acirc;y dựng THHT kh&ocirc;ng phải đơn giản.</span><br />\r\n<span style=\"color:#333333\">Tr&ecirc;n cơ sở ph&acirc;n t&iacute;ch THHT gi&aacute;o vi&ecirc;n tổ chức cho HS lập kế hoạt h&agrave;nh động để giải quyết vấn đề đ&atilde; xuất hiện trong THHT.</span><br />\r\n<span style=\"color:#333333\">Sản phẩm thu được của giai đoạn n&agrave;y l&agrave; bản kế hoạch thực hiện, m&agrave; bản th&acirc;n n&oacute; đ&atilde; được GV chuẩn bị trước khi v&agrave;o giờ giảng. Th&ocirc;ng thường n&oacute; bao gồm danh s&aacute;ch c&aacute;c kỹ năng cần h&igrave;nh th&agrave;nh, qui tr&igrave;nh thực hiện từng kỹ năng, định lượng thời gian l&agrave;m việc cho từng kỹ năng v&agrave; lượng kiến thức l&yacute; thuyết mới chen v&agrave;o khi thực hiện c&aacute;c qui tr&igrave;nh đ&oacute;. Ri&ecirc;ng GV cần lưu &yacute; thời điểm chen phần l&yacute; thuyết v&agrave;o giai đoạn của qu&aacute; tr&igrave;nh hoạt động sao cho khi HS cần GV đ&aacute;p ứng đ&uacute;ng thời điểm mới c&oacute; hiệu quả.</span><br />\r\n<span style=\"color:#333333\">Với quan niệm h&igrave;nh th&agrave;nh kỹ năng nghề nghiệp l&agrave; ch&iacute;nh, n&ecirc;n phần h&igrave;nh th&agrave;nh kỹ năng ph&acirc;n t&iacute;ch THHT v&agrave; lập kế hoạch kh&ocirc;ng d&agrave;nh qu&aacute; nhiều thời gian để thực hiện, GV chỉ cần tr&igrave;nh b&agrave;y qua nội dung v&agrave; đưa ra sản phẩm đ&atilde; chuẩn bị. Việc n&agrave;y được thực hiện nhiều lần sẽ dần h&igrave;nh th&agrave;nh cho HS th&oacute;i quen ph&acirc;n t&iacute;ch THHT v&agrave; lập kế hoạch cho bản th&acirc;n sau n&agrave;y, cũng như HS biết tại sao GV phải c&oacute; những sản phẩm đ&oacute;. Trường hợp đặc biệt, muốn ph&aacute;t huy t&iacute;nh t&iacute;ch cực của HS, GV c&oacute; thể tập trung tổ chức hoạt động n&agrave;y, nhưng điều đ&oacute; kh&ocirc;ng được khuyến kh&iacute;ch trong dạy học t&iacute;ch hợp. Bởi v&igrave;, c&oacute; thể HS c&oacute; thể x&acirc;y dựng qui tr&igrave;nh kh&aacute;c với qui tr&igrave;nh m&agrave; d&acirc;y chuyền sản xuất đang cần. Điều nay tr&ocirc;ng c&oacute; vẻ kh&ocirc;ng khoa học nhưng cần thống nhất quan điểm ở đ&acirc;y l&agrave; ch&uacute;ng ta đang cần những người thợ được đ&agrave;o tạo để l&agrave;m việc tại những vị tr&iacute; việc l&agrave;m m&agrave; c&ocirc;ng việc đ&atilde; định sẵn, kh&ocirc;ng kh&aacute;c đi được.</span><br />\r\n<span style=\"color:#333333\"><strong><em>1.3.3. Giai đoạn thứ ba: Tổ chức thực hiện theo kế hoạch đ&atilde; lập</em></strong></span><br />\r\n<span style=\"color:#333333\">Trong giai đoạn c&oacute; những việc phải l&agrave;m l&agrave;:</span><br />\r\n<span style=\"color:#333333\">- Thao t&aacute;c mẫu của GV</span><br />\r\n<span style=\"color:#333333\">- Tr&igrave;nh b&agrave;y tổng qu&aacute;t qui tr&igrave;nh đ&atilde; lập</span><br />\r\n<span style=\"color:#333333\">- Thao t&aacute;c thử của HS</span><br />\r\n<span style=\"color:#333333\">- Đ&aacute;nh gi&aacute; thao t&aacute;c thử của HS</span><br />\r\n<span style=\"color:#333333\">Lưu &yacute; c&aacute;c lỗi thường gặp, nguy&ecirc;n nh&acirc;n v&agrave; biện ph&aacute;p khắc phục, ph&ograve;ng tr&aacute;nh.</span><br />\r\n<span style=\"color:#333333\">- Trang bị kiến thức l&yacute; thuyết cần thiết.</span><br />\r\n<span style=\"color:#333333\">T&ugrave;y theo mức độ đơn giản hay phức tạp của vấn đề đặt ra m&agrave; c&aacute;c kỹ năng cần h&igrave;nh th&agrave;nh được tổ chức hợp l&yacute;.</span><br />\r\n<span style=\"color:#333333\"><em>a) Nếu vấn đề đơn giản:</em></span><br />\r\n<span style=\"color:#333333\">GV thao t&aacute;c mẫu qua một lần, tạo sản phẩm thuyết phục HS, tạo sự tin tưởng v&agrave; hứng th&uacute; HS, Tiếp theo GV chỉ lần tr&igrave;nh b&agrave;y kh&aacute;i qu&aacute;t qui tr&igrave;nh n&ecirc;n m&aacute;y chiếu hay bảng biểu in sẵn, sau đ&oacute; c&oacute; thể cho HS thao thử ngay. Khi HS thao t&aacute;c thử chắc chắn gặp kh&oacute; khăn, nh&acirc;n những điểm kh&oacute; khăn đ&oacute; m&agrave; GV ch&egrave;n l&yacute; thuyết mới v&agrave;o. Trước khi cho HS luyện tập GV chỉ cần lưu &yacute; một số lỗi thường gặp, ph&acirc;n t&iacute;ch c&aacute;c nguy&ecirc;n nh&acirc;n v&agrave; đưa&nbsp; ra khuyến c&aacute;o ph&ograve;ng tr&aacute;nh. Cuối c&ugrave;ng cho HS luyện tập, trong l&uacute;c luyện tập của HS, tại&nbsp; những điểm mấu chốt, GV ch&egrave;n th&ecirc;m một số l&yacute; thuyết nữa xem như ho&agrave;n chỉnh.</span><br />\r\n<span style=\"color:#333333\"><em>b) Nếu vấn đề tương đối phức tạp:</em></span><br />\r\n<span style=\"color:#333333\">Cũng thực hiện tương tự như tr&ecirc;n nhưng sau khi thao t&aacute;c mẫu một lần, thu sản phẩm thuyết phục HS rồi kh&ocirc;ng n&ecirc;n mời HS thao t&aacute;c thử ngay m&agrave; GV phải thao t&aacute;c mẫu th&ecirc;m lần nữa. Lần thao mẫu n&agrave;y thực hiện chậm v&agrave; c&oacute; dừng lại giữa c&aacute;c bước để giải th&iacute;ch r&otilde; về mặt thao t&aacute;c, về tại sao phải thao t&aacute;c như vậy m&agrave; kh&ocirc;ng l&agrave;m kh&aacute;c đi... Khi đ&oacute; c&oacute; thể chen l&yacute; thuyết v&agrave;o. C&aacute;c bước tiếp theo tương tự trường hợp tr&ecirc;n. C&aacute;i kh&oacute; ở đ&acirc;y l&agrave; vừa thao t&aacute;c mẫu phải chen l&yacute; thuyết v&agrave;o như thế n&agrave;o? Viết bảng, n&oacute;i miệng hay tr&igrave;nh b&agrave;y tr&ecirc;n m&aacute;y chiếu, bảng biểu treo tường. Việc n&agrave;y phụ thuộc đặc th&ugrave; của nghề m&agrave; &aacute;p dụng. C&oacute; những nghề ngay khi thao t&aacute;c, GV kh&ocirc;ng thể viết bảng v&igrave; l&yacute; d&otilde; vệ sinh, GV kh&ocirc;ng thể n&oacute;i miệng v&igrave; qu&aacute; ồn hoặc GV phải đeo khẩu trang, mặt nạ... Gặp t&igrave;nh n&agrave;o cụ thể sẽ b&agrave;n sau nh&eacute;!</span><br />\r\n<span style=\"color:#333333\"><em>c) Nếu vấn đề kh&aacute; phức tạp</em></span><br />\r\n<span style=\"color:#333333\">Đối với trường hợp n&agrave;y GV kh&ocirc;ng thể thao t&aacute;c mẫu ngay v&igrave; HS c&oacute; tập trung quan s&aacute;t cũng kh&ocirc;ng lĩnh hội được c&aacute;i g&igrave;, thậm ch&iacute; c&ograve;n c&oacute; suy nghĩ &ldquo;Ồ phức tạp qu&aacute;&ldquo;. Thay v&agrave;o đ&oacute;, GV phải:</span><br />\r\n<span style=\"color:#333333\">Tr&igrave;nh n&agrave;y tỉ mỉ qui tr&igrave;nh thực hiện: Việc n&agrave;y cụ thể l&agrave; GV phải c&oacute; bảng qui tr&igrave;nh chuẩn với đầy đủ c&aacute;c y&ecirc;u cầu như thứ tự c&aacute;c bước thực hiện c&ocirc;ng việc, y&ecirc;u cầu kỹ thuật đối với từng bước bằng những chỉ số đ&aacute;nh gi&aacute; cụ thể. Đối với những bước quan trọng, mấu chốt của qui tr&igrave;nh, GV phải tr&igrave;nh b&agrave;y tại sao phải thực hiện như vậy, phải ch&egrave;n l&yacute; thuyết v&agrave;o đ&uacute;ng vị tr&iacute; đ&oacute; v&agrave; đảm bảo HS lĩnh hội được. Nếu c&ograve;n trừu tượng GV phải c&oacute; h&igrave;nh ảnh, vật thật... để cụ thể h&oacute;a n&oacute; ra.</span><br />\r\n<span style=\"color:#333333\">Thực hiện c&ocirc;ng t&aacute;c chuẩn bị: Tr&ecirc;n cơ sở bước tr&ecirc;n, GV tiến h&agrave;nh c&ocirc;ng t&aacute;c chuẩn bị trước khi l&agrave;m mẫu. Chuẩn bị dụng cụ thiết bị, gi&aacute;o cụ trực quan, vật tư... tổ chức nơi l&agrave;m việc đảm bảo an to&agrave;n, vệ sinh v&agrave; mỹ quan... Thực hiện phần chuẩn bị sẽ gi&uacute;p HS hiểu r&otilde; hơn l&yacute; thuyết ở phần tr&ecirc;n đồng thời gi&uacute;p GV c&oacute; điều ki&ecirc;n thao t&aacute;c mẫu th&agrave;nh c&ocirc;ng.</span><br />\r\n<span style=\"color:#333333\">Ho&agrave;n tất hai nội dung n&agrave;y th&igrave; mức độ phức tạp của vấn đề đ&atilde; được đơn giản h&oacute;a, cho n&ecirc;n bước tiếp theo l&agrave; thực hiện tương tự trường hợp vấn đề đơn giản.</span><br />\r\n<span style=\"color:#333333\">Để t&igrave;m hiểu kỹ hơn về trường hợp vấn đề kh&aacute; phức tạp h&atilde;y nghi&ecirc;n cứu thẻ kỹ năng Tr&igrave;nh diễn một kỹ năng.</span><br />\r\n<span style=\"color:#333333\"><strong><em>1.3.4. Giai đoạn thứ tư: Tổ chức đ&aacute;nh gi&aacute;</em></strong></span><br />\r\n<span style=\"color:#333333\">Bước cuối c&ugrave;ng của dạy học định hướng hoạt động GV tổ chức đ&aacute;nh gi&aacute; qu&aacute; tr&igrave;nh giải quyết vấn đề. Nội dung đ&aacute;nh gi&aacute; bao gồm:</span><br />\r\n<span style=\"color:#333333\">Về kỹ năng: Mức độ h&igrave;nh th&agrave;nh c&aacute;c kỹ năng của b&agrave;i học. Th&ocirc;ng qua qu&aacute; tr&igrave;nh theo d&otilde;i HS luyện tập GV đ&atilde; nắm bắt thao t&aacute;c của từng HS, sản phẩm thu được của c&aacute;c em so với sản phẩm mẫu.</span><br />\r\n<span style=\"color:#333333\">Về kiến thức: Mức độ lĩnh hội c&aacute;c kiến thức l&yacute; thuyết mới cũng như mức độ vận dụng kiến thức đ&atilde; học v&agrave;o qu&aacute; tr&igrave;nh luyện tập.</span><br />\r\n<span style=\"color:#333333\">Về th&aacute;i độ: GV đ&atilde; quan s&aacute;t th&aacute;i độ học tập của HS từ giai đoạn đầu đến giai đoạn cuối ra sao, diễn biến t&acirc;m l&yacute; c&oacute; đ&uacute;ng như dự đo&aacute;n của GV kh&ocirc;ng. Th&aacute;i độ học tập của biểu hiện qua tinh thần học tập hăng say, t&iacute;ch cực hay thụ động, miễn cưỡng... t&ograve; m&ograve; khoa học, muốn hỏi nhiều điều hay chỉ dừng lại ở thắc mắc trong đầu...</span><br />\r\n<span style=\"color:#333333\">Ngo&agrave;i ra GV c&oacute; thể đ&aacute;nh gi&aacute; th&ecirc;m về tiến độ thời gian, về độ kh&oacute; của vấn đề tr&ecirc;n tinh thần động vi&ecirc;n HS học tốt hơn sau n&agrave;y.</span><br />\r\n<span style=\"color:#333333\"><strong><em><span style=\"color:blue\">2. Cấu tr&uacute;c chung của b&agrave;i giảng t&iacute;ch hợp</span></em></strong></span><br />\r\n<span style=\"color:#333333\">Qua kết quả ph&acirc;n t&iacute;ch tr&ecirc;n gi&aacute;o &aacute;n t&iacute;ch hợp đ&atilde; được h&igrave;nh th&agrave;nh theo cấu tr&uacute;c cơ bản như sau:&nbsp;<em><u>(Ch&uacute; &yacute; đ&acirc;y kh&ocirc;ng phải l&agrave; mẫu gi&aacute;o &aacute;n, n&oacute; ch&iacute;nh l&agrave; cơ sở khoa học định hướng mẫu gi&aacute;o &aacute;n)</u></em></span></span></span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellspacing=\"0\" class=\"Table\" style=\"-webkit-text-stroke-width:0px; background-color:#eeeeee; border-collapse:collapse; border-spacing:0px; border:1px solid windowtext; box-sizing:border-box; color:#333333\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:1px solid black; height:25px; vertical-align:top; width:208px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Cấu tr&uacute;c b&agrave;i dạy theo định hướng giải quyết vấn đề</span></strong></span></span></td>\r\n			<td colspan=\"2\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; height:25px; vertical-align:top; width:402px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Định hướng c&aacute;c hoạt động</span></strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:35px; vertical-align:top; width:212px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Gi&aacute;o vi&ecirc;n</span></strong></span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:35px; vertical-align:top; width:190px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Học sinh</span></strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:208px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Dẫn nhập:</span></strong><br />\r\n			THHT phải được m&ocirc; tả đầy đủ tr&ecirc;n giấy k&egrave;m HSBG ở phần thứ tư</span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:212px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tổ chức t&igrave;nh huống học tập hay c&aacute;c hoạt động tương tự.</span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:190px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tiếp cận THHT th&ocirc;ng qua tri gi&aacute;c bằng c&aacute;c gi&aacute;c quan.</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:610px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><em>Sản phẩm mong đợi của giai đoạn n&agrave;y l&agrave; HS x&aacute;c định được THHT gắn với t&igrave;nh huống sản xuất xảy ra tại vị tr&iacute; việc l&agrave;m của họ trong tương lai, với t&acirc;m trạng phấn khởi, t&ograve; m&ograve; khoa học.</em></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:118px; vertical-align:top; width:208px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Giới thiệu chủ đề:</span></strong><br />\r\n			(L&uacute;c n&agrave;y GV ghi ti&ecirc;u đề b&agrave;i học l&ecirc;n bảng, hoặc chiếu tr&ecirc;n m&aacute;y. Kh&ocirc;ng qu&ecirc;n n&oacute;i c&acirc;u:&nbsp;<em>&ldquo;Để đạt c&aacute;c mục ti&ecirc;u đ&oacute;,&nbsp;<u>ch&uacute;ng ta c&ugrave;ng nghi&ecirc;n cứu</u>&nbsp;b&agrave;i học mới...</em>&ldquo; v&agrave; &ldquo;<em>b&agrave;i học gồm c&aacute;c nội dung: ...</em>&nbsp;&ldquo;</span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:118px; vertical-align:top; width:212px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tổ chức ph&acirc;n t&iacute;ch THHT để to&aacute;t l&ecirc;n chủ đề v&agrave; c&aacute;c kỹ năng cần thiết cần h&igrave;nh th&agrave;nh trong b&agrave;i học.<br />\r\n			<br />\r\n			Tr&igrave;nh b&agrave;y c&aacute;c mục ti&ecirc;u của b&agrave;i học v&agrave; c&aacute;c nội dung cần lĩnh hội.</span></span><br />\r\n			&nbsp;</td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:118px; vertical-align:top; width:190px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Ph&acirc;n t&iacute;ch THHT để x&aacute;c định đ&uacute;ng chủ đề v&agrave; c&aacute;c kỹ năng cần thiết cần h&igrave;nh th&agrave;nh trong b&agrave;i học.<br />\r\n			Định hướng &aacute;p dụng THHT trong thực tế sản xuất tại vị tr&iacute; việc l&agrave;m.</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:86px; vertical-align:top; width:610px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><em>Sản phẩm mong đợi của giai đoạn n&agrave;y l&agrave; HS x&aacute;c định r&otilde; m&igrave;nh cần lĩnh hội kiến thức g&igrave;, h&igrave;nh th&agrave;nh kỹ năng g&igrave;. Những điều đ&oacute; được &aacute;p dụng tại tr&iacute; việc l&agrave;m n&agrave;o, v&agrave; c&oacute; hứng th&uacute;, quyết t&acirc;m đạt được điều đ&oacute;.</em></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:208px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Giải quyết vấn đề:</span></strong><br />\r\n			- Thao t&aacute;c mẫu của GV<br />\r\n			- Tr&igrave;nh b&agrave;y tổng qu&aacute;t qui tr&igrave;nh đ&atilde; lập<br />\r\n			- Thao t&aacute;c thử của HS<br />\r\n			- Đ&aacute;nh gi&aacute; thao thử của HS<br />\r\n			- Lưu &yacute; c&aacute;c lỗi thường gặp, nguy&ecirc;n nh&acirc;n v&agrave; biện ph&aacute;p khắc phục, ph&ograve;ng tr&aacute;nh.<br />\r\n			- Trang bị kiến thức l&yacute; thuyết cần thiết.</span></span><br />\r\n			&nbsp;</td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:212px\"><br />\r\n			<span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tổ chức c&aacute;c hoạt động&nbsp;<strong>t&ugrave;y theo mức độ phức tạp</strong>&nbsp;của vấn đề đ&atilde; ph&acirc;n t&iacute;ch ở tr&ecirc;n.</span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:10px; vertical-align:top; width:190px\"><br />\r\n			<span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Thực hiện c&aacute;c hoạt động&nbsp;<strong>tương ứng.</strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:113px; vertical-align:top; width:610px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><em>Sản phẩm của giai đoạn n&agrave;y l&agrave; c&aacute;c thao t&aacute;c, c&aacute;c kỹ năng nghề nghiệp được h&igrave;nh th&agrave;nh ở từng HS. C&aacute;c kiến thức l&yacute; thuyết được HS lĩnh&nbsp; hội đầy đủ, s&acirc;u sắc. Biểu hiện của sản phẩm c&oacute; thể dưới dạng vật chất (một sản hữu h&igrave;nh), phi vật chất (một quyết định, một dịch vụ, c&aacute;c thao t&aacute;c kỹ thuật...).</em></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:174px; vertical-align:top; width:208px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><strong><span style=\"color:blue\">Kết th&uacute;c vấn đề:</span></strong></span></span></td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:174px; vertical-align:top; width:212px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tổ chức đ&aacute;nh gi&aacute; tr&ecirc;n c&aacute;c mặt:</span></span>\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Kỹ năng;</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Kiến thức;</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Th&aacute;i độ;</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">C&aacute;c mặt kh&aacute;c.</span></span></li>\r\n			</ul>\r\n			</td>\r\n			<td style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; height:174px; vertical-align:top; width:190px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Thực hiện qu&aacute; tr&igrave;nh tự đ&aacute;nh gi&aacute;</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"background-color:#eeeeee; border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; height:233px; vertical-align:top; width:610px\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Sản phẩm cuối c&ugrave;ng:</span></span>\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Những kiến thức mới được HS lĩnh hội đầy đủ, s&acirc;u sắc</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Những kỹ năng mới được h&igrave;nh th&agrave;nh vững chắc</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Những phẩm chất đạo đức nghề nghiệp cần thiết tương ứng với c&ocirc;ng việc xuất hiện trong THHT cũng như trong vị tr&iacute; việc l&agrave;m trong tương lai.</span></span></li>\r\n			</ul>\r\n			<span style=\"font-size:14px\"> <span style=\"font-family:Verdana,Geneva,sans-serif\"> Biểu hiện cụ thể của sản phẩm:</span></span>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">THHT được giải quyết thuyết phục.</span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Verdana,Geneva,sans-serif\">Tinh thần, th&aacute;i độ học tập của HS vui vẻ, thoải m&aacute;i, v&agrave; mong đợi c&oacute; THHT mới.</span></span></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 119, 2, 1, 1, '2021-07-28 20:29:40', '2021-07-28 20:29:40');
INSERT INTO `posts` (`id`, `title`, `slug`, `introtext`, `thumbnail`, `content`, `views`, `category_id`, `user_id`, `group`, `created_at`, `updated_at`) VALUES
(15, '[Tài liệu học tập] Triển khai thực hiện Nghị quyết số 52-NQ/TW, ngày 27/9/2019 của Bộ Chính trị khóa XII', 'tai-lieu-hoc-tap-trien-khai-thuc-hien-nghi-quyet-so-52-nqtw-ngay-2792019-cua-bo-chinh-tri-khoa-xii-1628089224', 'Trường Cao đẳng Kinh tế và CNTP trân trọng giới thiệu toàn văn Nghị quyết số 52-NQ/TW ngày 27/9/2019 của Bộ Chính...', 'uploads/images/thumbnail_resize/t77467.jpg', '<p style=\"text-align:center\"><strong>T&Agrave;I LIỆU HỌC TẬP</strong></p>\r\n\r\n<p style=\"text-align:center\">Triển khai thực hiện Nghị quyết số 52-NQ/TW, ng&agrave;y 27/9/2019<br />\r\ncủa Bộ Ch&iacute;nh trị kh&oacute;a XII về một số chủ trương, ch&iacute;nh s&aacute;ch<br />\r\nchủ động tham gia cuộc c&aacute;ch mạng c&ocirc;ng nghiệp lần thứ Tư<br />\r\n<em>(K&egrave;m theo Hướng dẫn số 42-HD/BTGTU, ng&agrave;y 12 th&aacute;ng 6 năm 2020<br />\r\ncủa Ban Tuy&ecirc;n gi&aacute;o Quận ủy)</em></p>\r\n\r\n<p><strong>1. Về quan điểm</strong></p>\r\n\r\n<p>(1)- Chủ động, t&iacute;ch cực tham gia cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ Tư (CMCN 4.0) l&agrave; y&ecirc;u cầu tất yếu kh&aacute;ch quan; l&agrave; nhiệm vụ c&oacute; &yacute; nghĩa chiến lược đặc biệt quan trọng, vừa cấp b&aacute;ch vừa l&acirc;u d&agrave;i của cả hệ thống ch&iacute;nh trị v&agrave; to&agrave;n x&atilde; hội, gắn chặt với qu&aacute; tr&igrave;nh hội nhập quốc tế s&acirc;u rộng; đồng thời nhận thức đầy đủ, đ&uacute;ng đắn về nội h&agrave;m, bản chất của CMCN 4.0 để quyết t&acirc;m đổi mới tư duy v&agrave; h&agrave;nh động, coi đ&oacute; l&agrave; giải ph&aacute;p đột ph&aacute; với bước đi v&agrave; lộ tr&igrave;nh ph&ugrave; hợp l&agrave; cơ hội để Việt Nam bứt ph&aacute; trong ph&aacute;t triển kinh tế - x&atilde; hội.</p>\r\n\r\n<p>(2)- CMCN 4.0 mang lại cả cơ hội v&agrave; th&aacute;ch thức. Phải nắm bắt kịp thời, tận dụng hiệu quả c&aacute;c cơ hội để n&acirc;ng cao năng suất lao động, hiệu quả, sức cạnh tranh của nền kinh tế, hiệu lực, hiệu quả quản l&yacute; x&atilde; hội th&ocirc;ng qua nghi&ecirc;n cứu, chuyển giao v&agrave; ứng dụng mạnh mẽ c&aacute;c th&agrave;nh tựu ti&ecirc;n tiến của CMCN 4.0 cho c&aacute;c lĩnh vực của đời sống kinh tế - x&atilde; hội, nhất l&agrave; một số ng&agrave;nh, lĩnh vực trọng điểm, c&oacute; tiềm năng, lợi thế để l&agrave;m động lực cho tăng trưởng theo tinh thần bắt kịp, tiến c&ugrave;ng v&agrave; vượt l&ecirc;n ở một số lĩnh vực so với khu vực v&agrave; thế giới. Chủ động ph&ograve;ng ngừa, ứng ph&oacute; để hạn chế c&aacute;c t&aacute;c động ti&ecirc;u cực, bảo đảm quốc ph&ograve;ng, an ninh, an to&agrave;n, c&ocirc;ng bằng x&atilde; hội v&agrave; t&iacute;nh bền vững của qu&aacute; tr&igrave;nh ph&aacute;t triển đất nước.</p>\r\n\r\n<p>(3)- CMCN 4.0 y&ecirc;u cầu phải đổi mới tư duy về quản l&yacute; kinh tế, quản l&yacute; x&atilde; hội, x&acirc;y dựng, ho&agrave;n thiện thể chế cho ph&ugrave; hợp. Cần c&oacute; c&aacute;ch tiếp cận mở, s&aacute;ng tạo, cho th&iacute; điểm đối với những vấn đề thực tiễn mới đặt ra, tạo mọi thuận lợi cho đổi mới s&aacute;ng tạo. Tr&aacute;nh mọi biểu hiện b&agrave;ng quan, thiếu tự tin, thụ động, nhưng kh&ocirc;ng chủ quan, n&oacute;ng vội, duy &yacute; ch&iacute;.</p>\r\n\r\n<p>(4)- Ph&aacute;t huy tối đa c&aacute;c nguồn lực, bảo đảm đủ nguồn lực cho việc chủ động tham gia CMCN 4.0, x&aacute;c định nguồn lực b&ecirc;n trong l&agrave; quyết định, chiến lược, cơ bản l&acirc;u d&agrave;i; nguồn lực b&ecirc;n ngo&agrave;i l&agrave; quan trọng, đột ph&aacute;, bảo đảm sự l&atilde;nh đạo của Đảng, quản l&yacute; của Nh&agrave; nước, ph&aacute;t huy sức mạnh của to&agrave;n x&atilde; hội.</p>\r\n\r\n<p><strong>2. Về mục ti&ecirc;u</strong></p>\r\n\r\n<p>Mục ti&ecirc;u tổng qu&aacute;t: Tận dụng c&oacute; hiệu quả c&aacute;c cơ hội do CMCN 4.0 đem lại để th&uacute;c đẩy qu&aacute; tr&igrave;nh đổi mới m&ocirc; h&igrave;nh tăng trưởng, cơ cấu lại nền kinh tế gắn với thực hiện c&aacute;c đột ph&aacute; chiến lược v&agrave; hiện đại ho&aacute; đất nước; ph&aacute;t triển mạnh mẽ kinh tế số; ph&aacute;t triển nhanh v&agrave; bền vững dựa tr&ecirc;n khoa học - c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo v&agrave; nh&acirc;n lực chất lượng cao; n&acirc;ng cao chất lượng cuộc sống, ph&uacute;c lợi của người d&acirc;n; bảo đảm vững chắc quốc ph&ograve;ng, an ninh, bảo vệ m&ocirc;i trường sinh th&aacute;i.</p>\r\n\r\n<p>Một số mục ti&ecirc;u cụ thể đến năm 2025: Duy tr&igrave; xếp hạng về chỉ số đổi mới s&aacute;ng tạo to&agrave;n cầu (GII) thuộc 3 nước dẫn đầu ASEAN. X&acirc;y dựng được hạ tầng số đạt tr&igrave;nh độ ti&ecirc;n tiến của khu vực ASEAN; Internet băng th&ocirc;ng rộng phủ 100% c&aacute;c x&atilde;. Kinh tế số chiếm khoảng 20% GDP; năng suất lao động tăng b&igrave;nh qu&acirc;n tr&ecirc;n 7%/năm. Cơ bản ho&agrave;n th&agrave;nh chuyển đổi số trong c&aacute;c cơ quan đảng, nh&agrave; nước, Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội. Thuộc nh&oacute;m 4 nước dẫn đầu ASEAN trong xếp hạng ch&iacute;nh phủ điện tử theo đ&aacute;nh gi&aacute; của Li&ecirc;n hợp quốc. C&oacute; &iacute;t nhất 3 đ&ocirc; thị th&ocirc;ng minh tại 3 v&ugrave;ng kinh tế trọng điểm ph&iacute;a Bắc, ph&iacute;a Nam v&agrave; miền Trung.</p>\r\n\r\n<p>Một số mục ti&ecirc;u cụ thể đến năm 2030: Duy tr&igrave; xếp hạng về chỉ số Đổi mới s&aacute;ng tạo to&agrave;n cầu (GII) thuộc nh&oacute;m 40 nước dẫn đầu thế giới. Mạng di động 5G phủ s&oacute;ng to&agrave;n quốc; mọi người d&acirc;n được truy cập Internet băng th&ocirc;ng rộng với chi ph&iacute; thấp. Kinh tế số chiếm tr&ecirc;n 30% GDP; năng suất lao động tăng b&igrave;nh qu&acirc;n khoảng 7,5%/năm. Ho&agrave;n th&agrave;nh x&acirc;y dựng Ch&iacute;nh phủ số. H&igrave;nh th&agrave;nh một số chuỗi đ&ocirc; thị th&ocirc;ng minh tại c&aacute;c khu vực kinh tế trọng điểm ph&iacute;a Bắc, ph&iacute;a Nam v&agrave; miền Trung; từng bước kết nối với mạng lưới đ&ocirc; thị th&ocirc;ng minh trong khu vực v&agrave; thế giới.</p>\r\n\r\n<p>Tầm nh&igrave;n đến năm 2045: Việt Nam trở th&agrave;nh một trong những trung t&acirc;m sản xuất v&agrave; dịch vụ th&ocirc;ng minh, trung t&acirc;m khởi nghiệp, đổi mới s&aacute;ng tạo thuộc nh&oacute;m dẫn đầu khu vực Ch&acirc;u &Aacute;; c&oacute; năng suất lao động cao, c&oacute; đủ năng lực l&agrave;m chủ v&agrave; &aacute;p dụng c&ocirc;ng nghệ hiện đại trong tất cả c&aacute;c lĩnh vực kinh tế - x&atilde; hội, m&ocirc;i trường, quốc ph&ograve;ng, an ninh.</p>\r\n\r\n<p><strong>3. Về c&aacute;c nhiệm vụ chủ yếu</strong></p>\r\n\r\n<p>- Nhiệm vụ thứ nhất: Đổi mới tư duy, thống nhất nhận thức, tăng cường vai tr&ograve; l&atilde;nh đạo của Đảng, quản l&yacute; của Nh&agrave; nước, ph&aacute;t huy sự tham gia của Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội.</p>\r\n\r\n<p>+ N&acirc;ng cao nhận thức của c&aacute;c cấp uỷ đảng, ch&iacute;nh quyền về sự cấp thiết phải chủ động tham gia t&iacute;ch cực v&agrave; c&oacute; hiệu quả CMCN 4.0, coi đ&oacute; l&agrave; một nhiệm vụ trọng t&acirc;m. Gắn c&aacute;c mục ti&ecirc;u, nhiệm vụ tham gia CMCN 4.0 với c&aacute;c mục ti&ecirc;u, nhiệm vụ ph&aacute;t triển kinh tế - x&atilde; hội, bảo vệ m&ocirc;i trường, bảo đảm quốc ph&ograve;ng, an ninh của c&aacute;c cấp, c&aacute;c ng&agrave;nh.</p>\r\n\r\n<p>+ X&aacute;c định nội dung cốt l&otilde;i của ch&iacute;nh s&aacute;ch chủ động tham gia CMCN 4.0 của nước ta l&agrave; th&uacute;c đẩy ph&aacute;t triển khoa học, c&ocirc;ng nghệ v&agrave; đổi mới s&aacute;ng tạo tr&ecirc;n tất cả c&aacute;c ng&agrave;nh, lĩnh vực v&agrave; th&uacute;c đẩy chuyển đổi số quốc gia, trọng t&acirc;m l&agrave; ph&aacute;t triển kinh tế số, x&acirc;y dựng đ&ocirc; thị th&ocirc;ng minh, ch&iacute;nh quyền điện tử, tiến tới ch&iacute;nh quyền số.</p>\r\n\r\n<p>+ Nh&agrave; nước ưu ti&ecirc;n chuyển giao v&agrave; ứng dụng mạnh mẽ c&ocirc;ng nghệ mới trong c&aacute;c ng&agrave;nh, lĩnh vực; c&oacute; tr&aacute;ch nhiệm th&uacute;c đẩy nghi&ecirc;n cứu khoa học, c&ocirc;ng nghệ, tạo m&ocirc;i trường thuận lợi v&agrave; hỗ trợ c&oacute; trọng t&acirc;m, trọng điểm để người d&acirc;n v&agrave; doanh nghiệp l&agrave; c&aacute;c chủ thể quyết định tham gia CMCN 4.0.</p>\r\n\r\n<p>+ Ph&aacute;t huy sự tham gia c&oacute; hiệu quả của Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội, c&aacute;c đối tượng chịu t&aacute;c động của ch&iacute;nh s&aacute;ch v&agrave;o qu&aacute; tr&igrave;nh hoạch định v&agrave; gi&aacute;m s&aacute;t thực thi c&aacute;c ch&iacute;nh s&aacute;ch c&oacute; li&ecirc;n quan đến CMCN 4.0. X&acirc;y dựng cơ chế hợp t&aacute;c giữa Nh&agrave; nước v&agrave; c&aacute;c doanh nghiệp, hiệp hội doanh nghiệp trong x&acirc;y dựng v&agrave; thực thi c&aacute;c ch&iacute;nh s&aacute;ch.</p>\r\n\r\n<p>- Nhiệm vụ thứ hai: Ho&agrave;n thiện thể chế tạo thuận lợi cho chủ động tham gia CMCN 4.0 v&agrave; qu&aacute; tr&igrave;nh chuyển đổi số quốc gia.</p>\r\n\r\n<p>+ Ho&agrave;n thiện ph&aacute;p luật, trước hết l&agrave; ph&aacute;p luật về doanh nghiệp, khởi nghiệp s&aacute;ng tạo, sở hữu tr&iacute; tuệ, thương mại, đầu tư, kinh doanh để tạo điều kiện thuận lợi cho qu&aacute; tŕnh chuyển đổi số quốc gia v&agrave; ph&aacute;t triển c&aacute;c sản phẩm, dịch vụ, m&ocirc; h&igrave;nh kinh tế mới dựa tr&ecirc;n nền tảng c&ocirc;ng nghệ số, Internet v&agrave; kh&ocirc;ng gian mạng; đồng thời ngăn chặn kịp thời c&aacute;c t&aacute;c động ti&ecirc;u cực cả về kinh tế v&agrave; x&atilde; hội, bảo đảm quốc ph&ograve;ng, an ninh quốc gia, trật tự, an to&agrave;n x&atilde; hội tr&ecirc;n kh&ocirc;ng gian mạng. Bổ sung một số ng&agrave;nh nghề kinh doanh mới được ưu đ&atilde;i đầu tư để th&uacute;c đẩy tham gia CMCN 4.0. C&oacute; cơ chế cho doanh nghiệp nh&agrave; nước thực hiện đầu tư nghi&ecirc;n cứu ph&aacute;t triển c&ocirc;ng nghệ, đầu tư mạo hiểm, đầu tư v&agrave;o khởi nghiệp đổi mới s&aacute;ng tạo.</p>\r\n\r\n<p>+ X&acirc;y dựng cơ chế quản l&yacute; ph&ugrave; hợp với m&ocirc;i trường kinh doanh số, tạo thuận lợi cho đổi mới s&aacute;ng tạo. Sớm ban h&agrave;nh khung thể chế thử nghiệm c&oacute; kiểm so&aacute;t đối với c&aacute;c c&ocirc;ng nghệ, sản phẩm, dịch vụ, m&ocirc; h&igrave;nh kinh doanh mới h&igrave;nh th&agrave;nh từ CMCN 4.0. Quy định r&otilde; phạm vi kh&ocirc;ng gian v&agrave; thời gian thử nghiệm. Nghi&ecirc;n cứu, x&acirc;y dựng c&aacute;c khu thử nghiệm d&agrave;nh cho doanh nghiệp c&ocirc;ng nghệ theo m&ocirc; h&igrave;nh ti&ecirc;n tiến của thế giới. Thực hiện định danh, c&ocirc;ng nhận, x&acirc;y dựng hệ thống ti&ecirc;u chuẩn, quy chuẩn đối với c&aacute;c sản phẩm, c&ocirc;ng nghệ, m&ocirc; h&igrave;nh kinh doanh mới.</p>\r\n\r\n<p>+ Chủ động tham gia v&agrave;o c&aacute;c khu&ocirc;n khổ ph&aacute;p l&yacute; khu vực v&agrave; to&agrave;n cầu để ph&aacute;t triển kinh tế số. Ho&agrave;n thiện ph&aacute;p luật, ch&iacute;nh s&aacute;ch về dữ liệu, quản trị dữ liệu, tạo thuận lợi cho việc tạo dựng, kết nối, chia sẻ v&agrave; khai th&aacute;c dữ liệu bảo đảm an to&agrave;n, an ninh mạng trong nước, tiến tới kết nối với khu vực ASEAN v&agrave; quốc tế. X&acirc;y dựng h&agrave;nh lang ph&aacute;p l&yacute; cho định danh số v&agrave; x&aacute;c thực điện tử quốc gia; thiết lập khung danh t&iacute;nh số quốc gia.</p>\r\n\r\n<p>+ Ho&agrave;n thiện c&aacute;c ch&iacute;nh s&aacute;ch t&agrave;i ch&iacute;nh nhằm khuyến kh&iacute;ch, huy động mọi nguồn lực x&atilde; hội đầu tư cho c&aacute;c hoạt động nghi&ecirc;n cứu khoa học, ph&aacute;t triển v&agrave; ứng dụng c&ocirc;ng nghệ, đổi mới s&aacute;ng tạo. Sửa đổi c&aacute;c quy định về đầu tư theo hướng tạo thuận lợi cho c&aacute;c hoạt động thu h&uacute;t vốn, mua cổ phần, mua b&aacute;n s&aacute;p nhập doanh nghiệp c&ocirc;ng nghệ; c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i g&oacute;p vốn v&agrave;o c&aacute;c doanh nghiệp khởi nghiệp s&aacute;ng tạo.</p>\r\n\r\n<p>+ Ho&agrave;n thiện ph&aacute;p luật về sở hữu tr&iacute; tuệ, bảo hộ v&agrave; khai th&aacute;c hiệu quả, hợp l&yacute; c&aacute;c t&agrave;i sản tr&iacute; tuệ do Việt Nam tạo ra; khuyến kh&iacute;ch thương mại ho&aacute; v&agrave; chuyển giao quyền sở hữu tr&iacute; tuệ, đặc biệt l&agrave; đối với c&aacute;c s&aacute;ng chế tại Việt Nam tr&ecirc;n cơ sở tu&acirc;n thủ ph&aacute;p luật trong nước v&agrave; bảo đảm lợi &iacute;ch an ninh quốc gia. Khuyến kh&iacute;ch c&aacute;c c&ocirc;ng ty đa quốc gia đặt c&aacute;c cơ sở nghi&ecirc;n cứu v&agrave; ph&aacute;t triển tại Việt Nam.</p>\r\n\r\n<p>+ Ho&agrave;n thiện ph&aacute;p luật, ch&iacute;nh s&aacute;ch về t&agrave;i ch&iacute;nh - tiền tệ, thanh to&aacute;n điện tử, quản l&yacute; thuế v&agrave; c&aacute;c dịch vụ xuy&ecirc;n bi&ecirc;n giới ph&ugrave; hợp với xu thế ph&aacute;t triển của kinh tế số. Ho&agrave;n thiện ch&iacute;nh s&aacute;ch đặt h&agrave;ng sản xuất v&agrave; mua sắm c&ocirc;ng đối với c&aacute;c sản phẩm c&ocirc;ng nghệ số do Việt Nam sản xuất.</p>\r\n\r\n<p>+ Ban h&agrave;nh c&aacute;c ch&iacute;nh s&aacute;ch hạn chế c&aacute;c t&aacute;c động ti&ecirc;u cực của CMCN 4.0. Tạo lập h&agrave;nh lang ph&aacute;p l&yacute; cho việc triển khai c&aacute;c m&ocirc; h&igrave;nh lao động, việc l&agrave;m mới tr&ecirc;n nền tảng c&ocirc;ng nghệ số v&agrave; ho&agrave;n thiện ch&iacute;nh s&aacute;ch an sinh x&atilde; hội ph&ugrave; hợp với CMCN 4.0, xử l&yacute; kịp thời c&aacute;c th&aacute;ch thức đặt ra đối với ph&aacute;t triển x&atilde; hội. Th&uacute;c đẩy việc tham gia CMCN 4.0 để giải quyết t&igrave;nh trạng &ocirc; nhiễm m&ocirc;i trường, biến đổi kh&iacute; hậu v&agrave; nước biển d&acirc;ng.</p>\r\n\r\n<p>+ X&acirc;y dựng v&agrave; triển khai Chiến lược quốc gia về ph&aacute;t triển c&aacute;c doanh nghiệp c&ocirc;ng nghệ, ưu ti&ecirc;n ph&aacute;t triển c&aacute;c doanh nghiệp c&ocirc;ng nghệ số, doanh nghiệp c&ocirc;ng nghệ cao thực hiện thiết kế, s&aacute;ng tạo v&agrave; sản xuất tại Việt Nam.</p>\r\n\r\n<p>+ X&acirc;y dựng, ho&agrave;n thiện khung ph&aacute;p l&yacute; về ph&aacute;t triển đ&ocirc; thị th&ocirc;ng minh bền vững; hệ thống c&aacute;c ti&ecirc;u chuẩn, quy chuẩn quốc gia; hệ thống hạ tầng dữ liệu, hệ thống chỉ ti&ecirc;u đ&aacute;nh gi&aacute; hiệu quả hoạt động của đ&ocirc; thị th&ocirc;ng minh. X&aacute;c định r&otilde; lộ tr&igrave;nh th&iacute; điểm v&agrave; lựa chọn c&aacute;c đ&ocirc; thị th&iacute; điểm đ&ocirc; thị th&ocirc;ng minh; cho ph&eacute;p thực hiện th&iacute; điểm một số cơ chế đặc th&ugrave; trong qu&aacute; tr&igrave;nh triển khai th&iacute; điểm ph&aacute;t triển đ&ocirc; thị th&ocirc;ng minh bảo đảm t&iacute;nh hiệu quả v&agrave; ph&ugrave; hợp với điều kiện thực tế.</p>\r\n\r\n<p>- Nhiệm vụ thứ ba. Ch&iacute;nh s&aacute;ch ph&aacute;t triển cơ sở hạ tầng thiết yếu.</p>\r\n\r\n<p>+ Triển khai băng th&ocirc;ng rộng chất lượng cao tr&ecirc;n phạm vi to&agrave;n quốc. Khuyến kh&iacute;ch doanh nghiệp tư nh&acirc;n c&oacute; đủ năng lực tham gia x&acirc;y dựng hạ tầng viễn th&ocirc;ng v&agrave; c&aacute;c hạ tầng kh&aacute;c cho chuyển đổi số quốc gia.</p>\r\n\r\n<p>+X&acirc;y dựng v&agrave; ph&aacute;t triển đồng bộ hạ tầng dữ liệu quốc gia. H&igrave;nh th&agrave;nh hệ thống trung t&acirc;m dữ liệu quốc gia, c&aacute;c trung t&acirc;m dữ liệu v&ugrave;ng v&agrave; địa phương kết nối đồng bộ v&agrave; thống nhất. H&igrave;nh th&agrave;nh c&aacute;c hệ thống dữ liệu tin cậy, ổn định của Nh&agrave; nước v&agrave; doanh nghiệp. Đầu tư trang bị c&aacute;c hệ thống thiết bị thu thập, lưu trữ, xử l&yacute;, bảo vệ dữ liệu c&ocirc;ng.</p>\r\n\r\n<p>+ Quy hoạch x&acirc;y dựng hạ tầng thanh to&aacute;n số quốc gia theo hướng đồng bộ, thống nhất, d&ugrave;ng chung, tận dụng v&agrave; khai th&aacute;c c&oacute; hiệu quả hạ t&acirc;̀ng mạng lưới viễn th&ocirc;ng đ&ecirc;̉ tri&ecirc;̉n khai các dịch vụ thanh to&aacute;n cho người d&acirc;n với chi phí th&acirc;́p. Ho&agrave;n thiện cơ chế, ch&iacute;nh s&aacute;ch th&uacute;c đẩy mạnh mẽ thanh to&aacute;n kh&ocirc;ng d&ugrave;ng tiền mặt. Khuyến kh&iacute;ch c&aacute;c doanh nghiệp Việt Nam x&acirc;y dựng c&aacute;c hệ thống thanh to&aacute;n số. Quản l&yacute; v&agrave; kiểm so&aacute;t chặt chẽ c&aacute;c h&igrave;nh thức thanh to&aacute;n trực tuyến qua bi&ecirc;n giới.</p>\r\n\r\n<p>+ Đầu tư n&acirc;ng cấp hạ tầng kỹ thuật bảo đảm an to&agrave;n, an ninh mạng. X&acirc;y dựng v&agrave; triển khai c&oacute; hiệu quả Nghị quyết số 30-NQ/TW, ng&agrave;y 25/7/2018 của Bộ Ch&iacute;nh trị về Chiến lược an ninh mạng quốc gia.</p>\r\n\r\n<p>+ N&acirc;ng cấp hạ tầng c&aacute;c ng&agrave;nh, lĩnh vực quan trọng, thiết yếu đồng bộ, hiện đại đ&aacute;p ứng y&ecirc;u cầu &aacute;p dụng c&ocirc;ng nghệ của CMCN 4.0, trước hết l&agrave; hạ tầng năng lượng v&agrave; giao th&ocirc;ng. Bảo đảm vững chắc an ninh năng lượng quốc gia.</p>\r\n\r\n<p>- Nhiệm vụ thứ tư: Ch&iacute;nh s&aacute;ch ph&aacute;t triển v&agrave; n&acirc;ng cao năng lực đổi mới s&aacute;ng tạo quốc gia.</p>\r\n\r\n<p>+ Cơ cấu lại to&agrave;n diện hệ thống c&aacute;c cơ sở nghi&ecirc;n cứu khoa học c&ocirc;ng nghệ c&ocirc;ng lập. X&acirc;y dựng v&agrave; ph&aacute;t triển c&aacute;c trung t&acirc;m đổi mới s&aacute;ng tạo quốc gia, tập trung v&agrave;o c&aacute;c c&ocirc;ng nghệ cốt l&otilde;i của CMCN 4.0. N&acirc;ng cao hiệu quả đầu tư c&ocirc;ng cho hoạt động nghi&ecirc;n cứu khoa học c&ocirc;ng nghệ tr&ecirc;n cơ sở &aacute;p dụng m&ocirc; h&igrave;nh quản trị mới theo th&ocirc;ng lệ tốt của thế giới.</p>\r\n\r\n<p>+ &Aacute;p dụng c&aacute;c cơ chế, ch&iacute;nh s&aacute;ch đặc biệt, c&oacute; t&iacute;nh đột ph&aacute; đối với c&aacute;c trung t&acirc;m đổi mới s&aacute;ng tạo. Ph&aacute;t triển hệ thống đổi mới s&aacute;ng tạo quốc gia theo hướng lấy doanh nghiệp l&agrave;m trung t&acirc;m, trường đại học v&agrave; viện nghi&ecirc;n cứu l&agrave; chủ thể nghi&ecirc;n cứu mạnh. Khuyến kh&iacute;ch c&aacute;c trường đại học, doanh nghiệp, tổ chức trong nước v&agrave; nước ngo&agrave;i th&agrave;nh lập c&aacute;c trung t&acirc;m đổi mới s&aacute;ng tạo tại Việt Nam.</p>\r\n\r\n<p>+ Ban h&agrave;nh hệ thống ti&ecirc;u chuẩn, quy chuẩn quốc gia l&agrave;m nền tảng cho việc ứng dụng v&agrave; ph&aacute;t triển c&aacute;c c&ocirc;ng nghệ cốt l&otilde;i của CMCN 4.0trong sản xuất v&agrave; đời sống. Tạo lập đồng bộ v&agrave; kịp thời khung ph&aacute;p l&yacute; v&agrave; hệ thống c&aacute;c ch&iacute;nh s&aacute;ch để triển khai v&agrave; ph&aacute;t triển c&aacute;c c&ocirc;ng nghệ mới. X&acirc;y dựng v&agrave; triển khai c&aacute;c chương tr&igrave;nh hỗ trợ c&aacute;c doanh nghiệp nghi&ecirc;n cứu v&agrave; ứng dụng c&ocirc;ng nghệ, thực hiện chuyển đổi số, n&acirc;ng cao năng suất, chất lượng, hiệu quả, sức cạnh tranh của nền kinh tế.</p>\r\n\r\n<p>+ Ho&agrave;n thiện m&ocirc; h&igrave;nh v&agrave; cơ chế, ch&iacute;nh s&aacute;ch để tạo sự ph&aacute;t triển đột ph&aacute; đối với c&aacute;c khu c&ocirc;ng nghệ cao. Tr&ecirc;n cơ sở c&aacute;c khu c&ocirc;ng nghệ cao Ho&agrave; Lạc (H&agrave; Nội), Đ&agrave; Nẵng v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh, ph&aacute;t triển c&aacute;c khu đ&ocirc; thị s&aacute;ng tạo đạt đẳng cấp quốc tế. Th&uacute;c đẩy ph&aacute;t triển hệ sinh th&aacute;i khởi nghiệp s&aacute;ng tạo, đẩy nhanh th&agrave;nh lập c&aacute;c trung t&acirc;m khởi nghiệp s&aacute;ng tạo quốc gia, trước mắt tại H&agrave; Nội, Đ&agrave; Nẵng, Th&agrave;nh phố Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>- Nhiệm vụ thứ năm. Ch&iacute;nh s&aacute;ch ph&aacute;t triển nguồn nh&acirc;n lực.</p>\r\n\r\n<p>+ R&agrave; so&aacute;t tổng thể, thực hiện đổi mới nội dung v&agrave; chương tr&igrave;nh gi&aacute;o dục, đ&agrave;o tạo theo hướng ph&aacute;t triển năng lực tiếp cận, tư duy s&aacute;ng tạo v&agrave; khả năng th&iacute;ch ứng với m&ocirc;i trường c&ocirc;ng nghệ li&ecirc;n tục thay đổi v&agrave; ph&aacute;t triển; đưa v&agrave;o chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng nội dung kỹ năng số v&agrave; ngoại ngữ tối thiểu. Đổi mới c&aacute;ch dạy v&agrave; học tr&ecirc;n cơ sở &aacute;p dụng c&ocirc;ng nghệ số; lấy đ&aacute;nh gi&aacute; của doanh nghiệp l&agrave;m thước đo cho chất lượng đ&agrave;o tạo của c&aacute;c trường đại học trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin. Khuyến kh&iacute;ch c&aacute;c m&ocirc; h&igrave;nh gi&aacute;o dục, đ&agrave;o tạo mới dựa tr&ecirc;n c&aacute;c nền tảng số.</p>\r\n\r\n<p>+ C&oacute; cơ chế khuyến kh&iacute;ch v&agrave; ưu đ&atilde;i đối với c&aacute;c tổ chức, c&aacute; nh&acirc;n, doanh nghiệp c&ocirc;ng nghệ tham gia trực tiếp v&agrave;o qu&aacute; tr&igrave;nh gi&aacute;o dục v&agrave; đ&agrave;o tạo, tạo ra sản phẩm phục vụ cho nền kinh tế số. X&acirc;y dựng một số trung t&acirc;m gi&aacute;o dục, đ&agrave;o tạo xuất sắc về c&ocirc;ng nghệ theo h&igrave;nh thức hợp t&aacute;c c&ocirc;ng - tư. Tiếp tục ho&agrave;n thiện cơ chế, ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch, thu h&uacute;t, sử dụng nh&acirc;n t&agrave;i, nguồn nh&acirc;n lực chất lượng cao.</p>\r\n\r\n<p>+ Ph&aacute;t triển mạnh đ&agrave;o tạo nghề v&agrave; hỗ trợ đ&agrave;o tạo kỹ năng cho chuyển đổi c&ocirc;ng việc. Nh&agrave; nước c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ cho người lao động khi tham gia đ&agrave;o tạo lại, đ&agrave;o tạo n&acirc;ng cao chuy&ecirc;n m&ocirc;n v&agrave; kỹ năng để chuyển đổi c&ocirc;ng việc.</p>\r\n\r\n<p>+ H&igrave;nh th&agrave;nh mạng học tập mở của người Việt Nam. Thực hiện theo lộ tŕnh phổ cập kỹ năng số, kỹ năng bảo đảm an to&agrave;n, an ninh mạng đạt tr&igrave;nh độ cơ bản cho người d&acirc;n. Đẩy mạnh c&ocirc;ng t&aacute;c truyền th&ocirc;ng, n&acirc;ng cao nhận thức, x&acirc;y dựng văn ho&aacute; số trong cộng đồng.</p>\r\n\r\n<p>- Nhiệm vụ thứ s&aacute;u: Ch&iacute;nh s&aacute;ch ph&aacute;t triển c&aacute;c ng&agrave;nh v&agrave; c&ocirc;ng nghệ ưu ti&ecirc;n</p>\r\n\r\n<p>+ Tập trung ph&aacute;t triển c&aacute;c ng&agrave;nh ưu ti&ecirc;n c&oacute; mức độ sẵn s&agrave;ng cao như: C&ocirc;ng nghiệp c&ocirc;ng nghệ th&ocirc;ng tin, điện tử - viễn th&ocirc;ng; an to&agrave;n, an ninh mạng; c&ocirc;ng nghiệp chế tạo th&ocirc;ng minh; t&agrave;i ch&iacute;nh - ng&acirc;n h&agrave;ng; thương mại điện tử; n&ocirc;ng nghiệp số; du lịch số; c&ocirc;ng nghiệp văn ho&aacute; số; y tế; gi&aacute;o dục v&agrave; đ&agrave;o tạo.</p>\r\n\r\n<p>+ Ưu ti&ecirc;n nguồn lực cho triển khai một số chương tr&igrave;nh nghi&ecirc;n cứu trọng điểm quốc gia về c&aacute;c c&ocirc;ng nghệ ưu ti&ecirc;n, trọng t&acirc;m l&agrave;: C&ocirc;ng nghệ th&ocirc;ng tin v&agrave; truyền th&ocirc;ng, cơ điện tử, c&ocirc;ng nghệ mới trong lĩnh vực năng lượng, tr&iacute; tuệ nh&acirc;n tạo, c&ocirc;ng nghệ sinh học, điện tử y sinh.</p>\r\n\r\n<p>+ Nh&agrave; nước thực hiện ch&iacute;nh s&aacute;ch hỗ trợ c&aacute;c ng&agrave;nh v&agrave; c&ocirc;ng nghệ ưu ti&ecirc;n chủ yếu th&ocirc;ng qua đổi mới, ho&agrave;n thiện thể chế, tạo lập m&ocirc;i trường kinh doanh thuận lợi; hỗ trợ đầu tư cơ sở hạ tầng; ph&aacute;t triển nguồn nh&acirc;n lực; ph&aacute;t triển khoa học v&agrave; c&ocirc;ng nghệ; đặt h&agrave;ng mua sắm c&ocirc;ng.</p>\r\n\r\n<p>- Nhiệm vụ thứ bảy: Ch&iacute;nh s&aacute;ch hội nhập quốc tế.</p>\r\n\r\n<p>+ Mở rộng v&agrave; l&agrave;m s&acirc;u sắc hơn hợp t&aacute;c về khoa học, c&ocirc;ng nghệ với c&aacute;c đối t&aacute;c, đặc biệt l&agrave; c&aacute;c nước đối t&aacute;c chiến lược c&oacute; tr&igrave;nh độ khoa học c&ocirc;ng nghệ ti&ecirc;n tiến, đang đi đầu trong CMCN 4.0. Chủ động tham gia mạng lưới đổi mới s&aacute;ng tạo to&agrave;n cầu.</p>\r\n\r\n<p>+ Đẩy mạnh thu h&uacute;t v&agrave; sử dụng hiệu quả c&aacute;c nguồn lực từ nước ngo&agrave;i v&agrave; c&aacute;c đối t&aacute;c quốc tế cho hoạt động nghi&ecirc;n cứu, ứng dụng, đổi mới s&aacute;ng tạo, khởi nghiệp s&aacute;ng tạo, chuyển giao c&ocirc;ng nghệ.</p>\r\n\r\n<p>+ Ho&agrave;n thiện luật ph&aacute;p, ch&iacute;nh s&aacute;ch về đầu tư trực tiếp nước ngo&agrave;i theo hướng n&acirc;ng cao ti&ecirc;u chuẩn c&ocirc;ng nghệ, khuyến kh&iacute;ch h&igrave;nh thức li&ecirc;n doanh v&agrave; tăng cường li&ecirc;n kết, chuyển giao c&ocirc;ng nghệ giữa doanh nghiệp trong nước với c&aacute;c doanh nghiệp đầu tư trực tiếp nước ngo&agrave;i; quản l&yacute; chặt chẽ hoạt động của c&aacute;c doanh nghiệp dựa tr&ecirc;n nền tảng cung cấp dịch vụ xuy&ecirc;n quốc gia để bảo đảm m&ocirc;i trường kinh doanh b&igrave;nh đẳng trong nước. Ho&agrave;n thiện c&aacute;c quy định ph&aacute;p luật về đầu tư mạo hiểm c&oacute; vốn nước ngo&agrave;i.</p>\r\n\r\n<p>- Nhiệm vụ thứ t&aacute;m: Ch&iacute;nh s&aacute;ch th&uacute;c đẩy chuyển đổi số trong c&aacute;c cơ quan Đảng, Nh&agrave; nước, Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội.</p>\r\n\r\n<p>+ Ti&ecirc;n phong thực hiện chuyển đổi số mạnh mẽ trong c&aacute;c cơ quan đảng, Quốc hội, Ch&iacute;nh phủ, Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội bảo đảm thống nhất, kết nối li&ecirc;n th&ocirc;ng v&agrave; đồng bộ.</p>\r\n\r\n<p>+ X&acirc;y dựng cơ sở dữ liệu số của Ch&iacute;nh phủ v&agrave; c&aacute;c cấp ch&iacute;nh quyền, tạo điều kiện để mọi c&ocirc;ng d&acirc;n c&oacute; thể cập nhật th&ocirc;ng tin cần thiết về hoạt động của bộ m&aacute;y nh&agrave; nước. Đầu tư x&acirc;y dựng cơ sở hạ tầng thu thập, quản l&yacute; dữ liệu v&agrave; giao dịch tr&ecirc;n nền tảng Internet ở c&aacute;c cơ quan nh&agrave; nước.</p>\r\n\r\n<p>+ Ch&uacute; trọng x&acirc;y dựng đội ngũ c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức đ&aacute;p ứng y&ecirc;u cầu của CMCN 4.0; chuẩn ho&aacute; v&agrave; tăng cường năng lực đội ngũ c&aacute;n bộ quản l&yacute; nh&agrave; nước c&aacute;c cấp. Ph&acirc;n định r&otilde; chức năng, nhiệm vụ, cơ chế phối hợp giữa c&aacute;c cơ quan quản l&yacute; nh&agrave; nước trong thực hiện chuyển đổi số. Ho&agrave;n thiện c&aacute;c quy tr&igrave;nh, thủ tục h&agrave;nh ch&iacute;nh ph&ugrave; hợp với hoạt động của ch&iacute;nh quyền số, cắt giảm tối đa giao dịch trực tiếp.</p>\r\n\r\n<p><strong>4. Về tổ chức thực hiện</strong></p>\r\n\r\n<p>- Ban c&aacute;n sự đảng Ch&iacute;nh phủ l&atilde;nh đạo x&acirc;y dựng v&agrave; triển khai Chiến lược quốc gia về CMCN 4.0; ban h&agrave;nh, triển khai Đề &aacute;n chuyển đổi số quốc gia v&agrave; c&aacute;c đề &aacute;n, chương tr&igrave;nh, kế hoạch tham gia CMCN 4.0; chỉ đạo x&acirc;y dựng tr&igrave;nh cấp c&oacute; thẩm quyền ban h&agrave;nh cơ chế, ch&iacute;nh s&aacute;ch thử nghiệm c&oacute; kiểm so&aacute;t đối với c&aacute;c sản phẩm, m&ocirc; h&igrave;nh, kinh doanh mới. Ưu ti&ecirc;n bố tr&iacute; đủ nguồn lực thực hiện c&aacute;c nhiệm vụ n&ecirc;u trong Nghị quyết.</p>\r\n\r\n<p>- Đảng đo&agrave;n Quốc hội l&atilde;nh đạo nghi&ecirc;n cứu, sửa đổi, bổ sung, ho&agrave;n thiện ph&aacute;p luật để tạo thuận lợi cho tham gia CMCN 4.0 theo tinh thần của Nghị quyết; ban h&agrave;nh một số cơ chế thử nghiệm c&oacute; kiểm so&aacute;t để khuyến kh&iacute;ch ph&aacute;t triển c&aacute;c sản phẩm, dịch vụ v&agrave; m&ocirc; h&igrave;nh kinh doanh mới của nền kinh tế số.</p>\r\n\r\n<p>- C&aacute;c tỉnh uỷ, th&agrave;nh uỷ, ban đảng, ban c&aacute;n sự đảng, đảng đo&agrave;n, đảng uỷ trực thuộc Trung ương tổ chức học tập, qu&aacute;n triệt Nghị quyết tới c&aacute;n bộ, đảng vi&ecirc;n; x&acirc;y dựng chương tr&igrave;nh, kế hoạch, đề &aacute;n cụ thể triển khai thực hiện Nghị quyết; x&aacute;c định mục ti&ecirc;u, nhiệm vụ ứng dụng, ph&aacute;t triển nghệ th&ocirc;ng tin l&agrave; nội dung quan trọng trong kế hoạch ph&aacute;t triển kinh tế - x&atilde; hội 5 năm v&agrave; hằng năm; gắn nội dung ứng dụng, ph&aacute;t triển c&aacute;c c&ocirc;ng nghệ của CMCN 4.0với c&aacute;c chương tr&igrave;nh, dự &aacute;n ph&aacute;t triển kinh tế - x&atilde; hội của địa phương. Thường xuy&ecirc;n l&atilde;nh đạo, chỉ đạo c&ocirc;ng t&aacute;c ứng dụng, ph&aacute;t triển c&ocirc;ng nghệ của CMCN 4.0v&agrave; định kỳ b&aacute;o c&aacute;o cấp ủy cấp tr&ecirc;n về việc thực hiện Nghị quyết. Kiện to&agrave;n v&agrave; n&acirc;ng cao hiệu quả hoạt động của cơ quan, tổ chức của c&aacute;c tỉnh, th&agrave;nh phố trực thuộc Trung ương.</p>\r\n\r\n<p>- Mặt trận Tổ quốc Việt Nam v&agrave; c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội x&acirc;y dựng chương tr&igrave;nh, kế hoạch gi&aacute;m s&aacute;t việc thực hiện Nghị quyết; động vi&ecirc;n đo&agrave;n vi&ecirc;n, hội vi&ecirc;n t&iacute;ch cực học tập, n&acirc;ng cao tr&igrave;nh độ v&agrave; tham gia c&oacute; hiệu quả v&agrave;o CMCN 4.0.</p>\r\n\r\n<p>- Ban Kinh tế Trung ương chủ tr&igrave;, phối hợp với c&aacute;c cơ quan li&ecirc;n quan thường xuy&ecirc;n theo d&otilde;i, giám sát, kiểm tra, đ&ocirc;n đốc triển khai thực hiện Nghị quyết; định kỳ sơ kết, tổng kết việc thực hiện, b&aacute;o c&aacute;o Bộ Ch&iacute;nh trị, Ban B&iacute; thư kết quả thực hiện Nghị quyết./.</p>', 15, 1, 1, 1, '2020-07-08 17:00:00', '2021-08-04 15:00:24'),
(16, 'Đại hội các chi bộ nhiệm kỳ 2020-2022', 'dai-hoi-cac-chi-bo-nhiem-ky-2020-2022-1628091681', 'Vào hồi 08 giờ ngày 21 tháng 12 năm 2019, tại phòng họp trường Cao đẳng Kinh tế và Công nghệ thực phẩm đã tổ chức...', 'uploads/images/thumbnail_resize/Untitled.jpg', '<p>V&agrave;o hồi 08 giờ ng&agrave;y 21 th&aacute;ng 12 năm 2019, tại ph&ograve;ng họp trường Cao đẳng Kinh tế v&agrave; C&ocirc;ng nghệ thực phẩm đ&atilde; tổ chức đại hội c&aacute;c chi bộ nhiệm kỳ 2020-2022</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh đại hội c&aacute;c chi bộ:</strong></p>\r\n\r\n<p>Chi bộ H&agrave;nh ch&iacute;nh tổ chức - Khảo th&iacute; kiểm định chất lượng&amp;KHHTQT</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3408.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Ph&ograve;ng T&agrave;i ch&iacute;nh kế to&aacute;n</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3425.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3427.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Ph&ograve;ng Đ&agrave;o tạo</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3432.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3435.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Khoa Gi&aacute;o dục thể chất- Khoa học cơ bản</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3450.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ ph&ograve;ng C&ocirc;ng t&aacute;c học sinh - Quản trị đời sống</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3442.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Khoa điện - điện tử</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3462.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Khoa C&ocirc;ng nghệ thực phẩm, muối</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3469.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Khoa Kinh tế</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3478.jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Chi bộ Khoa C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/news/dai_hoi_chi_bo_2020/img_3495.jpg\" style=\"width:100%\" /></p>', 91, 1, 1, 1, '2019-12-20 17:00:00', '2021-08-04 15:41:21'),
(17, 'Công khai cơ sở vật chất, trang thiết bị đào tạo tại các cơ sở liên kết đào tạo', 'cong-khai-co-so-vat-chat-trang-thiet-bi-dao-tao-tai-cac-co-so-lien-ket-dao-tao-1627742955', 'Văn bản đăng ký bổ sung hoạt động giáo dục nghề nghiệp', 'uploads/images/thumbnail_resize/123197844_3495709400505133_8719524369004269322_n.jpg', '<p><iframe height=\"920\" src=\"http://docs.google.com/viewer?url=http%3A%2F%2Fmysite.home%2Fuploads%2Ffiles%2FV__n%20b___n%20____ng%20ky%20b___%20sung%20ho___t%20_____ng%20giao%20d___c%20ngh___%20nghi___p%20_1_%20_1_.docx&amp;embedded=true\" style=\"border: none;\" width=\"710\"></iframe></p>\r\n\r\n<p><iframe height=\"920\" src=\"http://docs.google.com/viewer?url=http%3A%2F%2Fmysite.home%2Fuploads%2Ffiles%2Fc___p%20phep%20ha%20t__nh.docx&amp;embedded=true\" style=\"border: none;\" width=\"710\"></iframe></p>', 97, 1, 1, 2, '2021-07-12 17:00:00', '2021-07-31 14:49:15'),
(18, 'Thông báo về việc thực hiện một số công việc về tổ chức đào tạo trong thời gian tới', 'thong-bao-ve-viec-thuc-hien-mot-so-cong-viec-ve-to-chuc-dao-tao-trong-thoi-gian-toi-1629039187', 'Căn cứ kết quả buổi sinh hoạt hè năm học 2020-2021, để đảm bảo thực hiện tốt\r\nhoạt động đào tạo của Nhà...', 'uploads/images/thumbnail_resize/unnamed.jpg', '<p><iframe height=\"920\" src=\"http://docs.google.com/viewer?url=http%3A%2F%2Fcdktcntp.0fees.us%2Fuploads%2Ffiles%2FThong%20bao.doc&amp;embedded=true\" style=\"border: none;\" width=\"100%\"></iframe></p>', 68, 2, 1, 2, '2021-07-28 20:30:19', '2021-08-16 01:53:07'),
(19, 'Tra cứu kết quả Kỳ thi tốt nghiệp THPT năm 2021', 'tra-cuu-ket-qua-ky-thi-tot-nghiep-thpt-nam-2021-1627549013', 'Kỳ thi tốt nghiệp THPT năm 2021, Hội đồng thi 03-Sở GD&ĐT Hải Phòng đã diễn ra thành công, an toàn và đúng quy chế.', 'uploads/images/thumbnail_resize/33b1a64da13656680f27_257202123.jpg', '<p><img alt=\"\" src=\"/uploads/images/news/33b1a64da13656680f27_257202123(1).jpg\" style=\"width:100%\" /></p>\r\n\r\n<p>Ngay sau khi điểm thi tốt nghiệp THPT năm 2021 được Bộ GD&amp;ĐT v&agrave; Sở GD&amp;ĐT Hải Ph&ograve;ng c&ocirc;ng bố, k&iacute;nh mời phụ huynh v&agrave; c&aacute;c em học sinh tra cứu bằng c&aacute;ch truy cập tại một trong c&aacute;c địa chỉ sau:</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:17px !important\"><span style=\"font-size:17.3333px !important\"><span style=\"color:#161616\"><a href=\"https://tradiem2021.haiphong.edu.vn/\" style=\"font-size:17px !important; line-height:26px !important\">https://tradiem2021.haiphong.edu.vn</a></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:17px !important\"><span style=\"font-size:17.3333px !important\"><span style=\"color:#161616\"><a href=\"https://diemthi2021.haiphong.edu.vn/\" style=\"font-size:17px !important; line-height:26px !important\">https://diemthi2021.haiphong.edu.vn</a></span></span></span></p>', 61, 1, 1, 1, '2021-07-24 17:00:00', '2021-07-29 08:56:53');

